import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Diff, Hunk, parseDiff } from "react-diff-view";
import "react-diff-view/style/index.css";
import "../styles/PRDetails.css"; // Assuming you have a CSS file for styling

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [openFiles, setOpenFiles] = useState({});

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPRDetails();
  }, [owner, repo, number]);

  const toggleFile = (filename) => {
    setOpenFiles((prev) => ({ ...prev, [filename]: !prev[filename] }));
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      alert("Diff copied to clipboard!");
    });
  };

  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{prDetails.details.number}:{" "}
          <span className="text-primary">{prDetails.details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <p>
        <strong>State:</strong>{" "}
        <span
          className={`badge ${
            prDetails.details.state === "open" ? "bg-success" : "bg-secondary"
          }`}
        >
          {prDetails.details.state.toUpperCase()}
        </span>
      </p>
      <p>
        <strong>Author:</strong> {prDetails.details.user?.login || "N/A"}
      </p>
      <p>
        <strong>Created At:</strong>{" "}
        {new Date(prDetails.details.created_at).toLocaleString()}
      </p>
      <p>
        <strong>Updated At:</strong>{" "}
        {new Date(prDetails.details.updated_at).toLocaleString()}
      </p>

      <hr />
      <h4>Description</h4>
      <p>{prDetails.details.body || "No description provided."}</p>

      <hr />
      <h4>Files Changed</h4>
      {prDetails.files.length === 0 && <p>No files changed.</p>}

      {prDetails.files.map((file) => {
        const diffText = `diff --git a/${file.filename} b/${file.filename}\n${file.patch}`;
        const files = parseDiff(diffText);
        const isOpen = openFiles[file.filename] ?? false;

        return (
          <div key={file.filename} className="mb-3 border rounded p-2">
            <div
              className="file-header d-flex justify-content-between align-items-center"
              onClick={() => toggleFile(file.filename)}
              style={{ cursor: "pointer" }}
            >
              <strong>{file.filename}</strong>
              <div>
                <button
                  className="btn btn-sm btn-outline-secondary me-2"
                  onClick={(e) => {
                    e.stopPropagation();
                    copyToClipboard(file.patch);
                  }}
                >
                  Copy Diff
                </button>
                <span>{isOpen ? "▼" : "▶"}</span>
              </div>
            </div>

            {isOpen && files[0]?.hunks?.length > 0 && (
              <Diff viewType="unified" diffType="modify" hunks={files[0].hunks}>
                {(hunks) =>
                  hunks.map((hunk) => (
                    <Hunk
                      key={hunk.content}
                      hunk={hunk}
                      renderMeta={() => (
                        <div className="hunk-header">{hunk.content}</div>
                      )}
                      renderLine={(line) => (
                        <div
                          className={`diff-line ${
                            line.type === "add"
                              ? "addition"
                              : line.type === "del"
                              ? "deletion"
                              : "context"
                          }`}
                        >
                          <span className="line-number">{line.oldNumber || ""}</span>
                          <span className="line-number">{line.newNumber || ""}</span>
                          <span className="line-content">{line.content}</span>
                        </div>
                      )}
                    />
                  ))
                }
              </Diff>
            )}

            {isOpen && (!files[0]?.hunks || files[0].hunks.length === 0) && (
              <p className="text-muted mt-2">No changes in this file.</p>
            )}
          </div>
        );
      })}
    </div>
  );
}
