          <h4 className="text-2xl font-bold text-white mt-1">{value}</h4>
        </div>
        <div
          className={`w-12 h-12 rounded-2xl bg-gradient-to-br ${ring} flex items-center justify-center shadow-lg`}
        >
          <Icon className="w-6 h-6 text-white" />
        </div>
      </div>

      
    </div>
    
  );
