import React, { useContext, useEffect, useRef } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/Home.css";

const Home = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const gradientRef = useRef(null);

  // Mouse movement effect
useEffect(() => {
  const handleMouseMove = (e) => {
    if (!gradientRef.current) return;
    const { innerWidth, innerHeight } = window;
    const x = (e.clientX / innerWidth) * 100;
    const y = (e.clientY / innerHeight) * 100;
    gradientRef.current.style.backgroundPosition = `${x}% ${y}%`;
  };

  window.addEventListener("mousemove", handleMouseMove);
  return () => window.removeEventListener("mousemove", handleMouseMove);
}, []);


  return (
    <div className="relative flex flex-col items-center justify-center h-screen text-center px-6 font-sans overflow-hidden">
      {/* Animated Gradient Background */}
      <div ref={gradientRef} className="absolute inset-0 interactive-gradient"></div>
      <div className="absolute inset-0 bg-black/50"></div> {/* overlay */}

      {/* Content */}
      <div className="relative z-10">
        <h1 className="text-4xl md:text-5xl font-bold text-white mb-4 tracking-tight">
          Welcome to <span className="text-indigo-400">GitMind</span>
        </h1>

        <p className="text-lg text-gray-300 mb-8">
          {user
            ? "You're all set, let's get started!"
            : "Click below to continue with GitHub"}
        </p>

        {user ? (
          <button
            onClick={() => navigate("/dashboard")}
            className="px-8 py-3 text-lg font-medium rounded-xl bg-green-600 text-white shadow-md hover:bg-green-700 hover:shadow-lg transition duration-300"
          >
            Dive In ðŸš€
          </button>
        ) : (
          <a href="http://localhost:5000/auth/github">
            <button className="px-8 py-3 text-lg font-medium rounded-xl bg-indigo-600 text-white shadow-md hover:bg-indigo-700 hover:shadow-lg transition duration-300">
              Login with GitHub
            </button>
          </a>
        )}
      </div>
    </div>
  );
};

export default Home;
