import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export default function PRList() {
  const { owner, repo } = useParams();
  const [prs, setPRs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRs = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(`http://localhost:5001/github/repos/${owner}/${repo}/pulls`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.ok) {
          throw new Error(`Failed to fetch PRs: ${res.status}`);
        }

        const data = await res.json();
        setPRs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRs();
  }, [owner, repo]);

  if (loading) {
    return <p className="text-center mt-5">Loading pull requests...</p>;
  }

  if (error) {
    return <p className="text-center text-danger mt-5">{error}</p>;
  }

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          Pull Requests — <span className="text-primary">{owner}/{repo}</span>
        </h2>
        <Link to="/dashboard" className="btn btn-secondary">
          ⬅ Back
        </Link>
      </div>

      {prs.length === 0 ? (
        <div className="alert alert-info">
          No pull requests have been raised for this repository.
        </div>
      ) : (
        <div className="row">
          {prs.map((pr) => (
            <div className="col-md-6 col-lg-4 mb-4" key={pr.id}>
              <div className="card shadow-sm border-0">
                <div className="card-body">
                  <h5 className="card-title">
                    <a
                      href={pr.html_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-decoration-none"
                    >
                      {pr.title}
                    </a>
                  </h5>
                  <p className="mb-1">
                    <small className="text-muted">
                      #{pr.number} opened by <strong>{pr.user.login}</strong>
                    </small>
                  </p>
                  <p className="mb-2">
                    <small className="text-muted">
                      Created: {new Date(pr.created_at).toLocaleDateString()}
                    </small>
                  </p>
                  <span
                    className={`badge ${
                      pr.state === "open" ? "bg-success" : "bg-secondary"
                    }`}
                  >
                    {pr.state.toUpperCase()}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
