import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="top-0 left-0 w-full z-50 bg-gradient-to-r from-[#020833] via-[#0a1a66] to-[#1a237e] backdrop-blur-md border-b border-white/10 shadow-lg">

      <div className="max-w-7xl mx-auto flex justify-between items-center px-6 py-3 font-sans">
        {/* Logo / Brand */}
        <Link
          to="/"
          className="text-2xl font-semibold text-indigo-400 tracking-wide hover:text-indigo-300 transition"
        >
          AI Review
        </Link>

        {/* Navigation Links */}
        <div className="flex items-center space-x-6">
          <Link
            to="/"
            className="text-white hover:text-indigo-400 transition font-medium"
          >
            Home
          </Link>

          {user && (
            <>
              <Link
                to="/dashboard"
                className="text-white hover:text-indigo-400 transition font-medium"
              >
                Dashboard
              </Link>
              <button
                onClick={logout}
                className="px-4 py-2 bg-red-500 text-white rounded-lg font-medium shadow-md hover:bg-red-600 hover:shadow-lg transition"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
