import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const CONTEXT_LINES = 20;

// ------------------ Parse patch into hunks ------------------
const parseHunks = (patch) => {
  const lines = patch.split("\n");
  const hunks = [];
  let currentHunk = null;
  let oldLine = 0, newLine = 0;

  lines.forEach((line) => {
    if (line.startsWith("@@")) {
      const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
      if (match) {
        oldLine = parseInt(match[1], 10) - 1;
        newLine = parseInt(match[2], 10) - 1;
      }
      if (currentHunk) hunks.push(currentHunk);
      currentHunk = { header: line, lines: [] };
    } else if (currentHunk) {
      let type = "unchanged";
      let displayOld = oldLine + 1;
      let displayNew = newLine + 1;

      if (line.startsWith("+")) {
        type = "added";
        displayOld = "";
        newLine++;
      } else if (line.startsWith("-")) {
        type = "removed";
        displayNew = "";
        oldLine++;
      } else {
        oldLine++;
        newLine++;
      }

      currentHunk.lines.push({ line, type, oldLine: displayOld, newLine: displayNew });
    }
  });

  if (currentHunk) hunks.push(currentHunk);
  return hunks;
};

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [expandedHunks, setExpandedHunks] = useState({});

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();

        // Auto-expand all hunks with initial context
        const initialExpanded = {};
        data.files.forEach((file) => {
          parseHunks(file.patch || "").forEach((_, hIdx) => {
            initialExpanded[`${file.filename}-${hIdx}`] = {
              above: CONTEXT_LINES,
              below: CONTEXT_LINES,
            };
          });
        });

        setExpandedHunks(initialExpanded);
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  const { details, files } = prDetails;

  // ------------------ Parse patch into hunks ------------------
  const parseHunks = (patch) => {
    const lines = patch.split("\n");
    const hunks = [];
    let currentHunk = null;
    let oldLine = 0, newLine = 0;

    lines.forEach((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
        if (currentHunk) hunks.push(currentHunk);
        currentHunk = { header: line, lines: [] };
      } else if (currentHunk) {
        let type = "unchanged";
        let displayOld = oldLine + 1;
        let displayNew = newLine + 1;

        if (line.startsWith("+")) {
          type = "added";
          displayOld = "";
          newLine++;
        } else if (line.startsWith("-")) {
          type = "removed";
          displayNew = "";
          oldLine++;
        } else {
          oldLine++;
          newLine++;
        }

        currentHunk.lines.push({ line, type, oldLine: displayOld, newLine: displayNew });
      }
    });

    if (currentHunk) hunks.push(currentHunk);
    return hunks;
  };

  // ------------------ Expand/Collapse Logic ------------------
  const toggleExpand = (file, hIdx, direction, expand = true) => {
    setExpandedHunks((prev) => {
      const key = `${file.filename}-${hIdx}`;
      const state = prev[key] || { above: 0, below: 0 };
      const hunks = parseHunks(file.patch || "");
      const hunk = hunks[hIdx];
      if (!hunk) return prev;

      const firstChanged = hunk.lines.findIndex((l) => l.type !== "unchanged");
      const lastChanged =
        hunk.lines.length - 1 -
        [...hunk.lines].reverse().findIndex((l) => l.type !== "unchanged");

      const newState = { ...state };
      if (direction === "above") {
        newState.above = expand
          ? Math.min(state.above + CONTEXT_LINES, firstChanged)
          : 0;
      } else {
        newState.below = expand
          ? Math.min(state.below + CONTEXT_LINES, hunk.lines.length - 1 - lastChanged)
          : 0;
      }

      return { ...prev, [key]: newState };
    });
  };

  const expandAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 1000, below: 1000 };
    });
    setExpandedHunks((prev) => ({ ...prev, ...updated }));
  };

  const collapseAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 0, below: 0 };
    });
    setExpandedHunks((prev) => ({ ...prev, ...updated }));
  };

  // ------------------ Render ------------------
  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{details.number}: <span className="text-primary">{details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <hr />
      <h4>Files Changed</h4>

      {files.map((file, fIdx) => {
        const hunks = parseHunks(file.patch || "");

        return (
          <details key={fIdx} open className="mb-3">
            <summary
              style={{
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.1rem",
                padding: "5px 10px",
                backgroundColor: "#f0f0f0",
                borderRadius: "5px",
              }}
            >
              {file.filename}
            </summary>

            <div style={{ margin: "10px 0", display: "flex", gap: "10px" }}>
              <button
                className="btn btn-sm btn-primary"
                onClick={() => expandAll(file)}
              >
                Expand All
              </button>
              <button
                className="btn btn-sm btn-secondary"
                onClick={() => collapseAll(file)}
              >
                Collapse All
              </button>
            </div>

            <div
              style={{
                maxHeight: "400px",
                overflow: "auto",
                border: "1px solid #ccc",
                borderRadius: "5px",
                marginTop: "5px",
                padding: "10px",
                backgroundColor: "#f6f8fa",
                fontFamily: "monospace",
              }}
            >
              {hunks.map((hunk, hIdx) => {
                const key = `${file.filename}-${hIdx}`;
                const state = expandedHunks[key] || {
                  above: CONTEXT_LINES,
                  below: CONTEXT_LINES,
                };

                const firstChanged = hunk.lines.findIndex(
                  (l) => l.type !== "unchanged"
                );
                const lastChanged =
                  hunk.lines.length - 1 -
                  [...hunk.lines]
                    .reverse()
                    .findIndex((l) => l.type !== "unchanged");

                const start = Math.max(0, firstChanged - state.above);
                const end = Math.min(
                  hunk.lines.length - 1,
                  lastChanged + state.below
                );

                const visibleLines = hunk.lines.slice(start, end + 1);

                // Show first commit from file.commits (placeholder — match hunk if needed)
                const commitForHunk = file.commits?.[0];

                return (
                  <div key={hIdx} style={{ marginBottom: "15px" }}>
                    <div style={{ fontWeight: "bold", color: "#555" }}>
                      {hunk.header}
                    </div>

                    {commitForHunk && (
                      <div
                        style={{
                          fontStyle: "italic",
                          color: "#333",
                          marginBottom: "5px",
                        }}
                      >
                        Commit: <b>{commitForHunk.sha.slice(0, 7)}</b> by{" "}
                        <b>{commitForHunk.author}</b> - {commitForHunk.message}
                      </div>
                    )}

                    <div style={{ marginBottom: "5px" }}>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "above", true)}
                      >
                        ⬆ Expand above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "above", false)}
                      >
                        ⬆ Collapse above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "below", true)}
                      >
                        ⬇ Expand below
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => toggleExpand(file, hIdx, "below", false)}
                      >
                        ⬇ Collapse below
                      </button>
                    </div>

                    {start > 0 && (
                      <div style={{ color: "#888" }}>
                        ... {start} hidden line(s) above ...
                      </div>
                    )}

                    {visibleLines.map((lineObj, i) => {
                      let style = {};
                      if (lineObj.type === "added")
                        style = { backgroundColor: "#d4f8d4" };
                      if (lineObj.type === "removed")
                        style = { backgroundColor: "#f8d4d4" };
                      return (
                        <div
                          key={i}
                          style={{ display: "flex", gap: "10px", ...style }}
                        >
                          <span
                            style={{
                              width: "50px",
                              textAlign: "right",
                              color: "#888",
                            }}
                          >
                            {lineObj.oldLine || ""}
                          </span>
                          <span
                            style={{
                              width: "50px",
                              textAlign: "right",
                              color: "#888",
                            }}
                          >
                            {lineObj.newLine || ""}
                          </span>
                          <span style={{ flex: 1 }}>{lineObj.line}</span>
                          {commitForHunk &&
                            (lineObj.type === "added" ||
                              lineObj.type === "removed") && (
                              <span
                                style={{
                                  fontStyle: "italic",
                                  color: "#555",
                                  marginLeft: "10px",
                                }}
                              >
                                {commitForHunk.author} |{" "}
                                {commitForHunk.sha.slice(0, 7)}
                              </span>
                            )}
                        </div>
                      );
                    })}

                    {end < hunk.lines.length - 1 && (
                      <div style={{ color: "#888" }}>
                        ... {hunk.lines.length - 1 - end} hidden line(s) below ...
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </details>
        );
      })}
    </div>
  );
}
