import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

export default function Dashboard() {
  const { logout, user } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchRepos = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch("http://localhost:5001/github/repos", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error(`Failed to fetch repos: ${res.status}`);
        }

        const data = await res.json();
        setRepos(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRepos();
  }, []);

  if (!user) {
    return <p className="text-center mt-5">Please log in to access the dashboard.</p>;
  }

  if (loading) {
    return (
      <div className="container text-center py-5">
        <div className="spinner-border text-primary" role="status"></div>
        <p className="mt-3">Loading repositories...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container text-center py-5">
        <p className="text-danger">Error: {error}</p>
        <button className="btn btn-secondary mt-3" onClick={logout}>
          Logout & Retry
        </button>
      </div>
    );
  }

  return (
    <div className="container py-4">
      {/* Header */}
      <div className="d-flex flex-column flex-sm-row justify-content-between align-items-center mb-4">
        <h2>{user?.displayName || user?.username || "User"}'s GitHub Repositories</h2>
        <button
          className="btn btn-danger mt-3 mt-sm-0"
          onClick={() => {
            localStorage.removeItem("authToken");
            logout();
          }}
        >
          Logout
        </button>
      </div>

      {/* Repo Cards Grid */}
      {repos.length > 0 ? (
        <div className="row">
          {repos.map((repo) => (
            <div key={repo.id} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title text-truncate">{repo.name}</h5>
                  <p className="card-text flex-grow-1 text-muted">
                    {repo.description || "No description provided"}
                  </p>
                  <a
                    href={repo.html_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-primary btn-sm mt-auto"
                  >
                    View Repo
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-muted">No repositories found.</p>
      )}
    </div>
  );
}
