import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import "../styles/Dashboard.css";

export default function PRList() {
  const { owner, repo } = useParams();
  const [prs, setPRs] = useState([]);
  const [filteredPRs, setFilteredPRs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("latest"); // latest or oldest
  const [prFilter, setPrFilter] = useState("all"); // all, open, closed, declined
  const navigate = useNavigate();
  const [cardsLoaded, setCardsLoaded] = useState(false);

  // Fetch PRs
  useEffect(() => {
    const fetchPRs = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (!res.ok) throw new Error(`Failed to fetch PRs: ${res.status}`);
        const data = await res.json();
        setPRs(data);
        setFilteredPRs(data);
        setCardsLoaded(true);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRs();
  }, [owner, repo]);

  // Apply search, sort, and filter
  useEffect(() => {
    let updatedPRs = [...prs];

    // Filter by PR state
    if (prFilter !== "all") {
      updatedPRs = updatedPRs.filter((pr) =>
        prFilter === "declined" ? pr.state === "closed" && pr.merged_at === null : pr.state === prFilter
      );
    }

    // Search by title
    if (searchTerm) {
      updatedPRs = updatedPRs.filter((pr) =>
        pr.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Sort by creation date
    updatedPRs.sort((a, b) =>
      sortOrder === "latest"
        ? new Date(b.created_at) - new Date(a.created_at)
        : new Date(a.created_at) - new Date(b.created_at)
    );

    setFilteredPRs(updatedPRs);
  }, [prs, searchTerm, sortOrder, prFilter]);

  if (loading)
    return (
      <div className="flex h-screen items-center justify-center">
        <p className="text-indigo-200 text-lg animate-pulse">Loading pull requests…</p>
      </div>
    );

  if (error)
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="px-5 py-4 rounded-2xl backdrop-blur bg-white/5 shadow-xl">
          <p className="text-red-300 font-semibold">⚠️ {error}</p>
          <Link
            to="/dashboard"
            className="mt-4 px-4 py-2 rounded-xl bg-red-600 hover:bg-red-700 text-white shadow-md"
          >
            Back to Dashboard
          </Link>
        </div>
      </div>
    );

  return (
    <div
      id="prlist-root"
      className="min-h-screen w-full flex flex-col px-5 md:px-8 py-8 text-white"
      style={{
        background: "linear-gradient(120deg, #00041dff 0%, #2b51ba 80%, #3b82f6 100%)",
      }}
    >
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-6">
        <h2 className="text-3xl font-bold tracking-wide">
          Pull Requests — <span className="text-indigo-200">{owner}/{repo}</span>
        </h2>
        <Link
          to="/dashboard"
          className="mt-4 md:mt-0 px-5 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
        >
          ⬅ Back
        </Link>
      </div>

      {/* Controls */}
      <div className="flex flex-col md:flex-row items-center gap-4 mb-6">
        {/* Search */}
        <input
          type="text"
          placeholder="Search PRs by title..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="px-4 py-2 rounded-lg bg-white/10 placeholder-white/70 text-white focus:outline-none focus:ring-2 focus:ring-indigo-400 transition duration-300 w-full md:w-1/2"
        />

        {/* Sort */}
        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          className="px-3 py-2 rounded-lg bg-white/10 text-white focus:outline-none focus:ring-2 focus:ring-indigo-400 transition duration-300"
        >
          <option value="latest">Sort by Latest</option>
          <option value="oldest">Sort by Oldest</option>
        </select>

        {/* Filter */}
        <select
          value={prFilter}
          onChange={(e) => setPrFilter(e.target.value)}
          className="px-3 py-2 rounded-lg bg-white/10 text-white focus:outline-none focus:ring-2 focus:ring-indigo-400 transition duration-300"
        >
          <option value="all">All PRs</option>
          <option value="open">Open</option>
          <option value="closed">Closed</option>
          <option value="declined">Declined</option>
        </select>
      </div>

      {/* PR Grid */}
      {filteredPRs.length === 0 ? (
        <p className="text-indigo-100/80">No pull requests found.</p>
      ) : (
        <div
          className={`grid gap-6 sm:gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 m-8 transition-opacity duration-700 ${
            cardsLoaded ? "opacity-100" : "opacity-0"
          }`}
        >
          {filteredPRs.map((pr, i) => (
            <div
              key={pr.id}
              className="rounded-2xl p-5 md:p-6 bg-white/5 
                         shadow-[16px_16px_27px_-7px_rgba(0,0,0,0.75)]
                         transform transition-transform duration-300 ease-in-out
                         flex flex-col
                         hover:scale-105
                         hover:shadow-[16px_16px_27px_-7px_rgba(255,255,255,0.55)]"
              style={{
                animation: `fadeInUp 0.6s ease forwards`,
                animationDelay: `${i * 90}ms`,
                minHeight: "140px",
              }}
            >
              <div className="flex items-center justify-between mb-1">
                <h3 className="text-lg md:text-xl font-semibold text-indigo-100 line-clamp-2">
                  {pr.title}
                </h3>
                <span
                  className={`inline-block whitespace-nowrap rounded-md text-xs font-medium px-2 py-0.5 ml-2 ${
                    pr.state === "open" ? "bg-green-600 text-white" : "bg-gray-600 text-gray-300"
                  }`}
                >
                  {pr.state.toUpperCase()}
                </span>
              </div>

              <p className="text-sm text-indigo-100/85 mt-2 line-clamp-2">
                #{pr.number} opened by <strong>{pr.user.login}</strong>
              </p>
              <p className="text-sm text-indigo-100/85 mt-1">
                Created: {new Date(pr.created_at).toLocaleDateString()}
              </p>

              <div className="mt-auto flex justify-end gap-3 pt-5">
                <button
                  onClick={() =>
                    navigate(`/repos/${owner}/${repo}/pulls/${pr.number}/details`)
                  }
                  className="px-4 py-2 border border-white text-white rounded-lg transition duration-300 hover:bg-white hover:text-black"
                >
                  View PR
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(10px); }
            100% { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
