import { motion } from "framer-motion";
import { Github, Linkedin, Twitter, Mail } from "lucide-react";

export default function Footer({
  brand = "AI PR Analyser",
  year = new Date().getFullYear(),
  links = [
    { label: "Home", href: "/" },
    { label: "Docs", href: "/docs" },
    { label: "About", href: "/about" },
    { label: "Contact", href: "/contact" },
  ],
  socials = [
    { icon: <Github size={18} />, href: "https://github.com/" },
    { icon: <Linkedin size={18} />, href: "https://linkedin.com/" },
    { icon: <Twitter size={18} />, href: "https://twitter.com/" },
    { icon: <Mail size={18} />, href: "mailto:someone@example.com" },
  ],
}) {
  return (
    <motion.footer
      initial={{ opacity: 0, y: 40 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="w-full bg-gradient-to-r from-indigo-900 via-purple-800 to-indigo-900 text-gray-200 py-8 px-6 mt-10 shadow-2xl"
    >
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 items-center">
        {/* Brand */}
        <div className="flex flex-col items-center md:items-start gap-2">
          <motion.h1
            whileHover={{ scale: 1.05 }}
            className="text-xl font-bold text-white"
          >
            {brand}
          </motion.h1>
          <p className="text-sm text-gray-400">
            Â© {year} {brand}. All rights reserved.
          </p>
        </div>

        {/* Links */}
        <div className="flex flex-wrap justify-center gap-4 text-sm font-medium">
          {links.map((link, i) => (
            <motion.a
              key={i}
              href={link.href}
              whileHover={{ scale: 1.1, color: "#ffffff" }}
              className="text-gray-300 hover:text-white transition"
            >
              {link.label}
            </motion.a>
          ))}
        </div>

        {/* Socials */}
        <div className="flex justify-center md:justify-end gap-4">
          {socials.map((s, i) => (
            <motion.a
              key={i}
              href={s.href}
              target="_blank"
              rel="noopener noreferrer"
              whileHover={{ scale: 1.2, rotate: 5 }}
              className="p-2 rounded-full bg-white/10 hover:bg-white/20 transition"
            >
              {s.icon}
            </motion.a>
          ))}
        </div>
      </div>
    </motion.footer>
  );
}
