import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const CONTEXT_LINES = 20;

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [expandedHunks, setExpandedHunks] = useState({});

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  const { details, files } = prDetails;

  const parseHunks = (patch) => {
    const lines = patch.split("\n");
    const hunks = [];
    let currentHunk = null;
    let oldLine = 0,
      newLine = 0;

    lines.forEach((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
        if (currentHunk) hunks.push(currentHunk);
        currentHunk = { header: line, lines: [] };
      } else if (currentHunk) {
        let type = "unchanged";
        let displayOld = oldLine + 1;
        let displayNew = newLine + 1;

        if (line.startsWith("+")) {
          type = "added";
          displayOld = "";
          newLine++;
        } else if (line.startsWith("-")) {
          type = "removed";
          displayNew = "";
          oldLine++;
        } else {
          oldLine++;
          newLine++;
        }

        currentHunk.lines.push({ line, type, oldLine: displayOld, newLine: displayNew });
      }
    });

    if (currentHunk) hunks.push(currentHunk);
    return hunks;
  };

  const toggleExpand = (fileName, hIdx, direction, expand = true) => {
    setExpandedHunks((prev) => {
      const key = `${fileName}-${hIdx}`;
      const state = prev[key] || { above: 0, below: 0 };
      const hunks = parseHunks(prDetails.files.find(f => f.filename === fileName)?.patch || "");
      const hunk = hunks[hIdx];
  
      if (!hunk) return prev;
  
      const firstChangedLineIndex = hunk.lines.findIndex(l => l.type !== "unchanged");
      const lastChangedLineIndex = hunk.lines.length - 1 - [...hunk.lines].reverse().findIndex(l => l.type !== "unchanged");
  
      let newState = { ...state };
  
      if (direction === "above") {
        const maxAbove = firstChangedLineIndex; // can't go above line 0
        newState.above = expand ? Math.min(state.above + CONTEXT_LINES, maxAbove) : 0;
      } else {
        const maxBelow = hunk.lines.length - 1 - lastChangedLineIndex; // can't go below last line
        newState.below = expand ? Math.min(state.below + CONTEXT_LINES, maxBelow) : 0;
      }
  
      return { ...prev, [key]: newState };
    });
  };
  
  const expandAll = (fileName, hunks) => {
    setExpandedHunks((prev) => {
      const updated = { ...prev };
      hunks.forEach((_, hIdx) => {
        updated[`${fileName}-${hIdx}`] = { above: 1000, below: 1000 };
      });
      return updated;
    });
  };

  const collapseAll = (fileName, hunks) => {
    setExpandedHunks((prev) => {
      const updated = { ...prev };
      hunks.forEach((_, hIdx) => {
        updated[`${fileName}-${hIdx}`] = { above: 0, below: 0 };
      });
      return updated;
    });
  };

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{details.number}: <span className="text-primary">{details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <hr />
      <h4>Files Changed</h4>

      {files.map((file, fIdx) => {
        const hunks = parseHunks(file.patch || "");

        return (
          <details key={fIdx} open className="mb-3">
            <summary
              style={{
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.1rem",
                padding: "5px 10px",
                backgroundColor: "#f0f0f0",
                borderRadius: "5px",
              }}
            >
              {file.filename}
            </summary>

            <div style={{ margin: "10px 0", display: "flex", gap: "10px" }}>
              <button className="btn btn-sm btn-primary" onClick={() => expandAll(file.filename, hunks)}>
                Expand All
              </button>
              <button className="btn btn-sm btn-secondary" onClick={() => collapseAll(file.filename, hunks)}>
                Collapse All
              </button>
            </div>

            <div
              style={{
                maxHeight: "400px",
                overflow: "auto",
                border: "1px solid #ccc",
                borderRadius: "5px",
                marginTop: "5px",
                padding: "10px",
                backgroundColor: "#f6f8fa",
                fontFamily: "monospace",
              }}
            >
              {hunks.map((hunk, hIdx) => {
                const key = `${file.filename}-${hIdx}`;
                const state = expandedHunks[key] || { above: 0, below: 0 };

                const firstChangedLineIndex = hunk.lines.findIndex((l) => l.type !== "unchanged");
                const lastChangedLineIndex =
                  hunk.lines.length -
                  1 -
                  [...hunk.lines].reverse().findIndex((l) => l.type !== "unchanged");

                const start = Math.max(0, firstChangedLineIndex - state.above);
                const end = Math.min(hunk.lines.length - 1, lastChangedLineIndex + state.below);

                return (
                  <div key={hIdx} style={{ marginBottom: "15px" }}>
                    <div style={{ fontWeight: "bold", color: "#555" }}>{hunk.header}</div>

                    <div style={{ marginBottom: "5px" }}>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file.filename, hIdx, "above", true)}
                      >
                        ⬆ Expand above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file.filename, hIdx, "above", false)}
                      >
                        ⬆ Collapse above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file.filename, hIdx, "below", true)}
                      >
                        ⬇ Expand below
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => toggleExpand(file.filename, hIdx, "below", false)}
                      >
                        ⬇ Collapse below
                      </button>
                    </div>

                    {hunk.lines.map((lineObj, i) => {
                      if (i < start || i > end) return null;

                      let style = {};
                      if (lineObj.type === "added") style = { backgroundColor: "#d4f8d4" };
                      if (lineObj.type === "removed") style = { backgroundColor: "#f8d4d4" };

                      return (
                        <div key={i} style={style}>
                          <span
                            style={{
                              display: "inline-block",
                              width: "50px",
                              textAlign: "right",
                              paddingRight: "10px",
                              color: "#888",
                            }}
                          >
                            {lineObj.oldLine || ""}
                          </span>
                          <span
                            style={{
                              display: "inline-block",
                              width: "50px",
                              textAlign: "right",
                              paddingRight: "10px",
                              color: "#888",
                            }}
                          >
                            {lineObj.newLine || ""}
                          </span>
                          <span>{lineObj.line}</span>
                        </div>
                      );
                    })}

                    {/* Hidden line indicator */}
                    {start > 0 && <div style={{ color: "#888" }}>... hidden lines above ...</div>}
                    {end < hunk.lines.length - 1 && <div style={{ color: "#888" }}>... hidden lines below ...</div>}
                  </div>
                );
              })}
            </div>
          </details>
        );
      })}
    </div>
  );
}
