import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext"; // Assuming you have this

export default function Dashboard() {
  const { user, logout } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRepos = async () => {
      try {
        const res = await fetch("http://localhost:5000/repos", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user?.token}`, // if your backend uses JWT
          },
          credentials: "include", // if using cookies
        });

        if (!res.ok) throw new Error("Failed to fetch repos");

        const data = await res.json();
        setRepos(data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    if (user) fetchRepos();
  }, [user]);

  if (loading) return <div className="text-center my-4">Loading repositories...</div>;

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>{user?.name || "User"}'s Repositories</h1>
        <button className="btn btn-danger btn-sm" onClick={logout}>
          Logout
        </button>
      </div>

      <div className="row">
        {repos.length > 0 ? (
          repos.map((repo, index) => (
            <div key={index} className="col-md-4 col-lg-3 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{repo.name}</h5>
                  <p className="card-text">
                    {repo.description || "No description provided"}
                  </p>
                  <a
                    href={repo.html_url}
                    className="btn btn-primary btn-sm"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View Repo
                  </a>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-12 text-center">No repositories found.</div>
        )}
      </div>
    </div>
  );
}
