import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prData, setPrData] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("jwtToken");

  useEffect(() => {
    const fetchPRDetails = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_URL}/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPrData(res.data);
      } catch (err) {
        console.error("Error fetching PR details:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPRDetails();
  }, [owner, repo, number, token]);

  if (loading) return <p>Loading...</p>;
  if (!prData) return <p>Failed to load PR details.</p>;

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">{prData.details.title}</h1>
      <p>Status: {prData.details.state}</p>
      <p>Author: {prData.details.user.login}</p>
      <p>Created: {new Date(prData.details.created_at).toLocaleString()}</p>

      <h2 className="text-lg font-semibold mt-4">Commits</h2>
      <ul className="list-disc pl-5">
        {prData.commits.map((commit) => (
          <li key={commit.sha}>
            {commit.commit.message} â€” {commit.commit.author.name}
          </li>
        ))}
      </ul>

      <h2 className="text-lg font-semibold mt-4">Files Changed</h2>
      {prData.files.map((file) => (
        <div key={file.filename} className="mb-4 border p-2 rounded">
          <p className="font-medium">{file.filename}</p>
          <p className="text-sm text-gray-600">
            +{file.additions} / -{file.deletions}
          </p>
          <pre className="bg-gray-900 text-green-300 p-2 rounded overflow-x-auto text-xs">
            {file.patch}
          </pre>
        </div>
      ))}
    </div>
  );
}
