import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prData, setPrData] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("jwtToken");

  useEffect(() => {
    const fetchPRDetails = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_URL}/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPrData(res.data);
      } catch (err) {
        console.error("Error fetching PR details:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPRDetails();
  }, [owner, repo, number, token]);

  if (loading) return <p>Loading...</p>;
  if (!prData) return <p>Failed to load PR details.</p>;

  const renderDiff = (patch) => {
    if (!patch) return <p className="text-gray-500">No diff available</p>;
    return patch.split("\n").map((line, idx) => {
      let bgClass = "";
      if (line.startsWith("+") && !line.startsWith("+++")) {
        bgClass = "bg-green-100 text-green-800";
      } else if (line.startsWith("-") && !line.startsWith("---")) {
        bgClass = "bg-red-100 text-red-800";
      } else {
        bgClass = "bg-gray-100 text-gray-800";
      }
      return (
        <div
          key={idx}
          className={`whitespace-pre-wrap font-mono text-sm px-2 py-0.5 ${bgClass}`}
        >
          {line}
        </div>
      );
    });
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-2">{prData.details.title}</h1>
      <p>Status: {prData.details.state}</p>
      <p>Author: {prData.details.user.login}</p>
      <p>Created: {new Date(prData.details.created_at).toLocaleString()}</p>

      <h2 className="text-lg font-semibold mt-4">Commits</h2>
      <ul className="list-disc pl-5 mb-4">
        {prData.commits.map((commit) => (
          <li key={commit.sha}>
            {commit.commit.message} â€” {commit.commit.author.name}
          </li>
        ))}
      </ul>

      <h2 className="text-lg font-semibold mt-4">Files Changed</h2>
      {prData.files.map((file) => (
        <div key={file.filename} className="mb-6 border rounded overflow-hidden">
          <div className="bg-gray-200 px-3 py-2 font-medium border-b">
            {file.filename} <span className="text-sm text-gray-600">(+{file.additions} / -{file.deletions})</span>
          </div>
          <div className="overflow-x-auto">{renderDiff(file.patch)}</div>
        </div>
      ))}
    </div>
  );
}
