import React, { useState } from "react";

const PRDetails = ({ analysis, analyzing, files }) => {
  const [expandedHunks, setExpandedHunks] = useState({});

  const parseHunks = (patch) => {
    const lines = patch.split("\n");
    const hunks = [];
    let currentHunk = null;

    lines.forEach((line) => {
      if (line.startsWith("@@")) {
        if (currentHunk) hunks.push(currentHunk);
        currentHunk = { header: line, lines: [] };
      } else if (currentHunk) {
        let type = "unchanged";
        if (line.startsWith("+")) type = "added";
        else if (line.startsWith("-")) type = "removed";

        currentHunk.lines.push({
          line,
          type,
          oldLine: line.startsWith("+") ? "" : null,
          newLine: line.startsWith("-") ? "" : null,
        });
      }
    });

    if (currentHunk) hunks.push(currentHunk);
    return hunks;
  };

  const toggleExpand = (file, hIdx, dir, expand) => {
    const key = `${file.filename}-${hIdx}`;
    setExpandedHunks((prev) => {
      const state = prev[key] || { above: 0, below: 0 };
      return {
        ...prev,
        [key]: {
          ...state,
          [dir]: expand ? state[dir] + 3 : Math.max(0, state[dir] - 3),
        },
      };
    });
  };

  const expandAll = (file) => {
    const hunks = parseHunks(file.patch || "");
    const updates = {};
    hunks.forEach((_, idx) => {
      updates[`${file.filename}-${idx}`] = { above: 9999, below: 9999 };
    });
    setExpandedHunks((prev) => ({ ...prev, ...updates }));
  };

  const collapseAll = (file) => {
    const hunks = parseHunks(file.patch || "");
    const updates = {};
    hunks.forEach((_, idx) => {
      updates[`${file.filename}-${idx}`] = { above: 0, below: 0 };
    });
    setExpandedHunks((prev) => ({ ...prev, ...updates }));
  };

  return (
    <div className="container-fluid mt-3">
      {/* Side by Side Layout */}
      <div style={{ display: "flex", gap: "20px", alignItems: "flex-start" }}>
        {/* Rule Engine Analysis */}
        <div style={{ flex: 1 }}>
          <div className="card mb-4">
            <div className="card-header bg-dark text-white">
              Rule Engine Analysis
            </div>
            <div className="card-body">
              {analyzing && <p>Analyzing PR...</p>}
              {!analyzing && analysis && (
                <>
                  <p>
                    <strong>{analysis.summary}</strong>
                  </p>

                  {analysis.findings && analysis.findings.length > 0 ? (
                    <ul style={{ listStyle: "none", paddingLeft: 0 }}>
                      {analysis.findings.map((result, idx) => {
                        let location = "";
                        if (result.line) location = `:${result.line}`;
                        else if (result.startLine && result.endLine)
                          location = `:${result.startLine}-${result.endLine}`;

                        let color = "#007bff";
                        if (result.severity === "warning") color = "orange";
                        else if (result.severity === "critical") color = "red";
                        else if (result.severity === "safe") color = "green";

                        let message = result.message || "";
                        if (result.type === "function_summary") {
                          message = result.function
                            ? `${result.function} â€” ${result.impact} (added: ${result.addedLines}, removed: ${result.removedLines})`
                            : "";
                        }
                        if (!message || message.trim() === "") return null;

                        return (
                          <li key={idx} style={{ marginBottom: "8px" }}>
                            <span style={{ color, fontWeight: "bold" }}>
                              [{result.severity.toUpperCase()}]
                            </span>{" "}
                            <strong>
                              {result.file}
                              {location}
                            </strong>{" "}
                            â€” {message}
                          </li>
                        );
                      })}
                    </ul>
                  ) : (
                    <p className="text-muted">
                      âœ… No issues found by rule engine
                    </p>
                  )}
                </>
              )}
            </div>
          </div>
        </div>

        {/* Files Changed */}
        <div style={{ flex: 2 }}>
          <h4>Files Changed</h4>
          {files.map((file, fIdx) => {
            const hunks = parseHunks(file.patch || "");
            return (
              <details key={fIdx} open className="mb-3">
                <summary
                  style={{
                    cursor: "pointer",
                    fontWeight: "bold",
                    fontSize: "1.1rem",
                    padding: "5px 10px",
                    backgroundColor: "#f0f0f0",
                    borderRadius: "5px",
                  }}
                >
                  {file.filename}
                </summary>

                <div
                  style={{
                    margin: "10px 0",
                    display: "flex",
                    gap: "10px",
                  }}
                >
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => expandAll(file)}
                  >
                    Expand All
                  </button>
                  <button
                    className="btn btn-sm btn-secondary"
                    onClick={() => collapseAll(file)}
                  >
                    Collapse All
                  </button>
                </div>

                <div
                  style={{
                    maxHeight: "400px",
                    overflowY: "auto",
                    overflowX: "auto", // ðŸ‘ˆ horizontal scroll
                    border: "1px solid #ccc",
                    borderRadius: "5px",
                    marginTop: "5px",
                    padding: "10px",
                    backgroundColor: "#f6f8fa",
                    fontFamily: "monospace",
                    whiteSpace: "pre", // ðŸ‘ˆ ensures no wrapping
                  }}
                >
                  {hunks.map((hunk, hIdx) => {
                    const key = `${file.filename}-${hIdx}`;
                    const state = expandedHunks[key] || { above: 0, below: 0 };

                    const firstChanged = hunk.lines.findIndex(
                      (l) => l.type !== "unchanged"
                    );
                    const lastChanged =
                      hunk.lines.length -
                      1 -
                      [...hunk.lines].reverse().findIndex(
                        (l) => l.type !== "unchanged"
                      );

                    const start = Math.max(0, firstChanged - state.above);
                    const end = Math.min(
                      hunk.lines.length - 1,
                      lastChanged + state.below
                    );

                    const visibleLines = hunk.lines.slice(start, end + 1);

                    return (
                      <div key={hIdx} style={{ marginBottom: "15px" }}>
                        <div style={{ fontWeight: "bold", color: "#555" }}>
                          {hunk.header}
                        </div>

                        <div style={{ marginBottom: "5px" }}>
                          <button
                            className="btn btn-sm btn-outline-secondary me-2"
                            onClick={() =>
                              toggleExpand(file, hIdx, "above", true)
                            }
                          >
                            â¬† Expand above
                          </button>
                          <button
                            className="btn btn-sm btn-outline-secondary me-2"
                            onClick={() =>
                              toggleExpand(file, hIdx, "above", false)
                            }
                          >
                            â¬† Collapse above
                          </button>
                          <button
                            className="btn btn-sm btn-outline-secondary me-2"
                            onClick={() =>
                              toggleExpand(file, hIdx, "below", true)
                            }
                          >
                            â¬‡ Expand below
                          </button>
                          <button
                            className="btn btn-sm btn-outline-secondary"
                            onClick={() =>
                              toggleExpand(file, hIdx, "below", false)
                            }
                          >
                            â¬‡ Collapse below
                          </button>
                        </div>

                        {start > 0 && (
                          <div style={{ color: "#888" }}>
                            ... {start} hidden line(s) above ...
                          </div>
                        )}

                        {visibleLines.map((lineObj, i) => {
                          let style = {};
                          if (lineObj.type === "added")
                            style = { backgroundColor: "#9FFFA0" };
                          if (lineObj.type === "removed")
                            style = { backgroundColor: "#FFA0A0" };
                          return (
                            <div key={i} style={style}>
                              <span
                                style={{
                                  display: "inline-block",
                                  width: "50px",
                                  textAlign: "right",
                                  paddingRight: "10px",
                                  color: "#000000",
                                }}
                              >
                                {lineObj.oldLine || ""}
                              </span>
                              <span
                                style={{
                                  display: "inline-block",
                                  width: "50px",
                                  textAlign: "right",
                                  paddingRight: "10px",
                                  color: "#000000",
                                }}
                              >
                                {lineObj.newLine || ""}
                              </span>
                              <span>{lineObj.line}</span>
                            </div>
                          );
                        })}

                        {end < hunk.lines.length - 1 && (
                          <div style={{ color: "#888" }}>
                            ... {hunk.lines.length - 1 - end} hidden line(s)
                            below ...
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </details>
            );
          })}
        </div>
      </div>

      {/* AI Summary Section FULL WIDTH BELOW */}
      {analysis && analysis.aiSummary && (
        <div className="card mt-4">
          <div className="card-header bg-info text-white">ðŸ¤– AI Summary</div>
          <div className="card-body">
            {analysis.aiSummary.split("\n").map((line, idx) => {
              if (line.match(/^[A-Za-z ]+:/)) {
                return (
                  <div key={idx} className="mb-2">
                    <h6 className="fw-bold text-primary">
                      {line.split(":")[0]}
                    </h6>
                    <p className="mb-1">
                      {line.split(":").slice(1).join(":").trim()}
                    </p>
                  </div>
                );
              }
              return <p key={idx}>{line}</p>;
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default PRDetails;
