export default function Dashboard() {
  const { logout } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [filteredRepos, setFilteredRepos] = useState([]);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [cardsLoaded, setCardsLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const gradientRef = useRef(null);

  // Fetch profile & repos
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      setError("No authentication token found.");
      setLoading(false);
      return;
    }

    const fetchData = async () => {
      try {
        const profileRes = await fetch("http://localhost:5001/github/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!profileRes.ok) throw new Error("Failed to fetch profile");
        const profileData = await profileRes.json();
        setProfile(profileData);

        const reposRes = await fetch("http://localhost:5001/github/repos", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!reposRes.ok) throw new Error("Failed to fetch repos");
        const reposData = await reposRes.json();
        setRepos(reposData);
        setFilteredRepos(reposData);

        setCardsLoaded(true);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Filter repos on search
  useEffect(() => {
    const filtered = repos.filter((repo) =>
      repo.name.toLowerCase().includes(search.toLowerCase())
    );
    setFilteredRepos(filtered);
  }, [search, repos]);

  if (loading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <p className="text-indigo-200 text-lg animate-pulse">Loading dashboard‚Ä¶</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="px-5 py-4 rounded-2xl backdrop-blur bg-white/5 shadow-xl">
          <p className="text-red-300 font-semibold">‚ö†Ô∏è {error}</p>
          <button
            onClick={logout}
            className="mt-4 px-4 py-2 rounded-xl bg-red-600 hover:bg-red-700 text-white shadow-md"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div
      id="dashboard-root"
      className="min-h-screen w-full flex flex-col px-5 md:px-8 py-8 text-white"
      style={{
        background:
          "linear-gradient(120deg, #00041dff 0%, #2b51ba 80%, #3b82f6 100%)",
      }}
    >
      {/* Top bar */}
      <div className="flex items-center justify-between mb-8">
        <h3 className="text-xl md:text-3xl font-bold tracking-tight">
          <span className="text-indigo-200">Repositories</span>
        </h3>
        <button
          onClick={() => {
            localStorage.removeItem("authToken");
            logout();
          }}
          className="px-4 py-2 rounded-xl bg-rose-500 hover:bg-rose-600 text-white shadow-md"
        >
          Logout
        </button>
      </div>

      {/* Profile */}
      {profile && (
        <div className="flex flex-col sm:flex-row items-start sm:items-center gap-6 mb-6">
          <img
            src={profile.avatar_url}
            alt="avatar"
            className="w-24 h-24 rounded-full ring-2 ring-white/50 shadow-lg"
          />
          <div className="space-y-2">
            <div className="flex items-center gap-4">
              <h3 className="text-3xl md:text-4xl font-bold tracking-tight">
                {profile.name || profile.login}
              </h3>
              {profile.login && (
                <a
                  href={profile.html_url}
                  target="_blank"
                  rel="noreferrer"
                  className="text-base text-indigo-200/90 hover:underline"
                >
                  @{profile.login}
                </a>
              )}
            </div>
            <p className="text-base text-indigo-100/90">
              {profile.bio || "No bio available"}
            </p>
            <div className="flex flex-wrap gap-5 text-sm md:text-base text-indigo-100/80">
              {profile.location && <span>üìç {profile.location}</span>}
              <span>üë• {profile.followers} followers</span>
              <span>üîó {profile.following} following</span>
              <span>üì¶ {profile.public_repos} public repos</span>
            </div>
          </div>
        </div>
      )}

      {/* Search bar */}
      <div className="mb-6">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Search repositories..."
          className="w-full md:w-1/2 px-4 py-2 rounded-lg text-black focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />
      </div>

      {/* Repo grid */}
      {filteredRepos.length === 0 ? (
        <p className="text-indigo-100/80">No repositories found.</p>
      ) : (
        <div
          className={`grid gap-6 sm:gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 transition-opacity duration-700`}
        >
          {filteredRepos.map((repo, i) => (
            <div
              key={repo.id}
              className="rounded-2xl p-5 md:p-6 bg-white/5 
                        shadow-[16px_16px_27px_-7px_rgba(0,0,0,0.75)]
                        transform transition-transform duration-300 ease-in-out 
                        flex flex-col 
                        hover:scale-105 hover:shadow-[16px_16px_27px_-7px_rgba(255,255,255,0.6)]"
              style={{
                animation: `fadeInUp 0.6s ease forwards`,
                animationDelay: `${i * 90}ms`,
              }}
            >
              <h3 className="text-lg md:text-xl font-semibold text-indigo-100">
                {repo.name}
              </h3>
              <p className="text-sm text-indigo-100/85 mt-2 line-clamp-2">
                {repo.description || "No description provided"}
              </p>

              <div className="flex gap-5 mt-4 text-xs text-indigo-100/70">
                <span>‚≠ê {repo.stargazers_count}</span>
                <span>üç¥ {repo.forks_count}</span>
                <span>üëÄ {repo.watchers_count}</span>
              </div>

              {/* Buttons always bottom-right */}
              <div className="mt-auto flex justify-end gap-3 pt-5">
                <button
                  onClick={() => window.open(repo.html_url, "_blank")}
                  className="button-74"
                >
                  View Repo
                </button>

                <button
                  onClick={() =>
                    navigate(`/repos/${repo.owner.login}/${repo.name}/prs`)
                  }
                  className="px-4 py-2 border border-white text-white rounded-lg transition duration-300 hover:bg-white hover:text-black"
                >
                  View PRs
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(10px); }
            100% { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
