import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const { logout, user } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRepos = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch("http://localhost:5001/github/repos", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.ok) throw new Error(`Failed to fetch repos: ${res.status}`);

        const data = await res.json();
        setRepos(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRepos();
  }, []);

// üé® Gradient mouse effect
useEffect(() => {
  let targetAngle = 0;
  let currentAngle = 0;
  let rafId;

  const lerp = (start, end, factor) => start + (end - start) * factor;

  const updateGradient = () => {
    // Smoothly interpolate towards target angle
    currentAngle = lerp(currentAngle, targetAngle, 0.10);

    document.body.style.background = `
      linear-gradient(
        ${currentAngle}deg,
        rgba(127, 128, 123, 0.85),
        rgba(8, 107, 255, 1) 50%,
        rgba(17, 23, 90, 1) 100%
      )
    `;

    rafId = requestAnimationFrame(updateGradient);
  };

  const handleMouseMove = (e) => {
    const { innerWidth } = window;
    const x = e.clientX / innerWidth; // 0 ‚Üí 1
    targetAngle = Math.round(360 * x); // update target
  };

  window.addEventListener("mousemove", handleMouseMove);
  rafId = requestAnimationFrame(updateGradient);

  return () => {
    window.removeEventListener("mousemove", handleMouseMove);
    cancelAnimationFrame(rafId);
  };
}, []);


  if (!user) {
    return (
      <div className="flex justify-center items-center h-screen text-white font-sans">
        <p className="text-lg text-gray-300">Please log in to access the dashboard.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="flex flex-col justify-center items-center h-screen text-white font-sans">
        <div className="w-12 h-12 border-4 border-indigo-500 border-t-transparent rounded-full animate-spin"></div>
        <p className="mt-4 text-indigo-400">Loading repositories...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col justify-center items-center h-screen text-white font-sans">
        <p className="text-red-400 text-lg font-medium">‚ö†Ô∏è {error}</p>
        <button
          className="mt-6 px-6 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300 
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
          onClick={logout}
        >
          Logout & Retry
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen text-white px-6 py-10 font-sans transition-colors duration-500">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-12">
        <h2 className="text-3xl font-bold tracking-wide">
          {user?.displayName || user?.username || "User"}'s{" "}
          <span className="text-indigo-300">Repositories</span>
        </h2>
        <button
          className="mt-4 md:mt-0 px-5 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
          onClick={() => {
            localStorage.removeItem("authToken");
            logout();
          }}
        >
          Logout
        </button>
      </div>

      {/* Repo Cards Grid */}
      {repos.length > 0 ? (
<div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 mt-[15%] mb-[15%] mx-[5%]">
  {repos.map((repo) => (
    <div
      key={repo.id}
      className="group relative p-6 rounded-md scale-[1.1]
      bg-gradient-to-br from-[#0a1a66] via-[#0d1b4d] to-[#020833]
      border border-indigo-700/40 
      shadow-[0_4px_15px_rgba(0,0,0,0.5)] 
      hover:scale-110 hover:shadow-[0_12px_30px_rgba(99,102,241,0.5)] 
      hover:border-indigo-400/60 
      transition duration-500 ease-out"
    >
      <h5 className="text-lg font-semibold mb-2 group-hover:text-indigo-300 transition">
        {repo.name}
      </h5>
      <p className="text-gray-400 text-sm mb-4 line-clamp-3">
        {repo.description ? repo.description : "No description provided"}
      </p>
      <div className="flex flex-wrap gap-3">
        <a
          href={repo.html_url}
          target="_blank"
          rel="noopener noreferrer"
          className="px-3 py-1.5 rounded-lg bg-indigo-600/20 border border-indigo-600 text-indigo-300 
          hover:bg-indigo-600/40 hover:transition duration-300"
        >
          View Repo
        </a>
        <button
          onClick={() =>
            navigate(`/repos/${repo.owner.login}/${repo.name}/prs`)
          }
          className="px-3 py-1.5 rounded-lg bg-gray-700/30 border border-gray-500 text-gray-300 
          hover:bg-gray-600/50 hover:transition duration-300"
        >
          View PRs
        </button>
      </div>
    </div>
  ))}
</div>

): (
        <p className="text-gray-400 text-center text-lg mt-12">
          No repositories found.
        </p>
      )}
    </div>
  );
}
