import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (!res.ok) {
          throw new Error(`Failed to fetch PR details: ${res.status}`);
        }

        const data = await res.json();

        // ✅ Extract `details` if backend wraps data
        if (data.details) {
          setPrDetails(data.details);
        } else {
          setPrDetails(data);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  if (loading) {
    return <p className="text-center mt-5">Loading PR details...</p>;
  }

  if (error) {
    return <p className="text-center text-danger mt-5">{error}</p>;
  }

  if (!prDetails) {
    return <p className="text-center mt-5">No details found for this PR.</p>;
  }

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{prDetails.number}:{" "}
          <span className="text-primary">{prDetails.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <p>
        <strong>State:</strong>{" "}
        {prDetails.state ? (
          <span
            className={`badge ${
              prDetails.state === "open" ? "bg-success" : "bg-secondary"
            }`}
          >
            {prDetails.state.toUpperCase()}
          </span>
        ) : (
          <span className="badge bg-warning text-dark">UNKNOWN</span>
        )}
      </p>
      <p>
        <strong>Author:</strong> {prDetails.user?.login || "N/A"}
      </p>
      <p>
        <strong>Created At:</strong>{" "}
        {prDetails.created_at
          ? new Date(prDetails.created_at).toLocaleString()
          : "N/A"}
      </p>
      <p>
        <strong>Updated At:</strong>{" "}
        {prDetails.updated_at
          ? new Date(prDetails.updated_at).toLocaleString()
          : "N/A"}
      </p>
      <hr />
      <h4>Description</h4>
      <p>{prDetails.body || "No description provided."}</p>
    </div>
  );
}
