import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";

const Dashboard = () => {
  const { user, logout, githubToken } = useContext(AuthContext); // assuming githubToken is stored
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(true);

  if (!user) {
    return <p className="text-center mt-10">Please log in to access the dashboard.</p>;
  }

  useEffect(() => {
    const fetchRepos = async () => {
      try {
        const res = await fetch(
          `http://localhost:5001/api/github/repos?token=${githubToken}`
        );
        const data = await res.json();
        setRepos(data);
      } catch (error) {
        console.error("Error fetching repos:", error);
      } finally {
        setLoading(false);
      }
    };

    if (githubToken) {
      fetchRepos();
    }
  }, [githubToken]);

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <button
          onClick={logout}
          className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-lg"
        >
          Logout
        </button>
      </div>

      <p className="text-lg mb-6">Welcome, {user.login || user.sub || "User"}!</p>

      {loading ? (
        <p>Loading repositories...</p>
      ) : repos.length === 0 ? (
        <p>No repositories found.</p>
      ) : (
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {repos.map((repo) => (
            <div
              key={repo.id}
              className="bg-white border border-gray-200 rounded-xl shadow-md p-5 hover:shadow-lg transition-shadow"
            >
              <h2 className="text-xl font-semibold mb-2">{repo.name}</h2>
              <p className="text-gray-600 mb-4">
                {repo.description || "No description provided."}
              </p>
              <a
                href={repo.html_url}
                target="_blank"
                rel="noreferrer"
                className="inline-block px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-lg"
              >
                View on GitHub
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
