import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const CONTEXT_LINES = 5; // Number of lines to expand above/below

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [expandedFiles, setExpandedFiles] = useState({}); // track expanded lines per file

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  const { details, files } = prDetails;

  const toggleExpand = (fileName, direction, index) => {
    setExpandedFiles((prev) => {
      const fileState = prev[fileName] || { above: 0, below: 0 };
      const change = direction === "above" ? Math.min(CONTEXT_LINES, index) : CONTEXT_LINES;
      const updated = {
        ...fileState,
        above: direction === "above" ? fileState.above + change : fileState.above,
        below: direction === "below" ? fileState.below + change : fileState.below,
      };
      return { ...prev, [fileName]: updated };
    });
  };

  const parsePatch = (patch) => {
    const lines = patch.split("\n");
    let oldLine = 0, newLine = 0;
    return lines.map((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
        return { type: "hunk", line, oldLine: "", newLine: "" };
      } else if (line.startsWith("+")) {
        newLine++;
        return { type: "added", line, oldLine: "", newLine };
      } else if (line.startsWith("-")) {
        oldLine++;
        return { type: "removed", line, oldLine, newLine: "" };
      } else {
        oldLine++;
        newLine++;
        return { type: "unchanged", line, oldLine, newLine };
      }
    });
  };

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{details.number}: <span className="text-primary">{details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <hr />
      <h4>Files Changed</h4>
      {files.map((file, idx) => {
        const patchLines = parsePatch(file.patch || "");
        const expandState = expandedFiles[file.filename] || { above: 0, below: 0 };

        return (
          <details key={idx} open className="mb-3">
            <summary
              style={{
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.1rem",
                padding: "5px 10px",
                backgroundColor: "#f0f0f0",
                borderRadius: "5px",
              }}
            >
              {file.filename}
            </summary>
            <div
              style={{
                maxHeight: "400px",
                overflow: "auto",
                border: "1px solid #ccc",
                borderRadius: "5px",
                marginTop: "5px",
                padding: "10px",
                backgroundColor: "#f6f8fa",
                fontFamily: "monospace",
              }}
            >
              {patchLines.map((lineObj, i) => {
                const showLine =
                  lineObj.type !== "unchanged" ||
                  (lineObj.type === "unchanged" &&
                    i >= Math.max(0, i - expandState.above) &&
                    i <= Math.min(patchLines.length - 1, i + expandState.below));

                if (!showLine) return null;

                let style = {};
                if (lineObj.type === "added") style = { backgroundColor: "#d4f8d4" };
                if (lineObj.type === "removed") style = { backgroundColor: "#f8d4d4" };
                if (lineObj.type === "hunk") style = { fontWeight: "bold", color: "#555" };

                return (
                  <div key={i} style={style}>
                    {i === 0 && (
                      <div style={{ marginBottom: "5px" }}>
                        <button
                          className="btn btn-sm btn-outline-secondary me-2"
                          onClick={() => toggleExpand(file.filename, "above", i)}
                        >
                          ⬆ Expand above
                        </button>
                        <button
                          className="btn btn-sm btn-outline-secondary"
                          onClick={() => toggleExpand(file.filename, "below", i)}
                        >
                          ⬇ Expand below
                        </button>
                      </div>
                    )}
                    <span style={{ display: "inline-block", width: "50px", textAlign: "right", paddingRight: "10px", color: "#888" }}>
                      {lineObj.oldLine || ""}
                    </span>
                    <span style={{ display: "inline-block", width: "50px", textAlign: "right", paddingRight: "10px", color: "#888" }}>
                      {lineObj.newLine || ""}
                    </span>
                    <span>{lineObj.line}</span>
                  </div>
                );
              })}
            </div>
          </details>
        );
      })}
    </div>
  );
}
