import React, { useState } from "react";
import axios from "axios";

export default function PullRequestList({ prs, token }) {
  const [selectedPR, setSelectedPR] = useState(null);
  const [prDetails, setPrDetails] = useState(null);

  const handleViewDetails = async (pr) => {
    setSelectedPR(pr.number);
    try {
      const res = await axios.get(
        `http://localhost:5000/api/github/repos/${pr.base.repo.owner.login}/${pr.base.repo.name}/pulls/${pr.number}`,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setPrDetails(res.data);
    } catch (err) {
      console.error("Error fetching PR details:", err);
    }
  };

  return (
    <div>
      {prs.length === 0 ? (
        <p>No pull requests found for this repository.</p>
      ) : (
        prs.map((pr) => (
          <div key={pr.id} style={{ border: "1px solid #ddd", padding: "10px", margin: "10px" }}>
            <h3>{pr.title}</h3>
            <p>Status: {pr.state}</p>
            <button onClick={() => handleViewDetails(pr)}>View Details</button>

            {selectedPR === pr.number && prDetails && (
              <div style={{ marginTop: "15px" }}>
                <h4>Commits</h4>
                <ul>
                  {prDetails.commits.map((commit) => (
                    <li key={commit.sha}>
                      <strong>{commit.commit.message}</strong> â€” {commit.commit.author.name} on{" "}
                      {new Date(commit.commit.author.date).toLocaleString()}
                    </li>
                  ))}
                </ul>

                <h4>File Changes</h4>
                {prDetails.files.map((file) => (
                  <div key={file.filename} style={{ marginBottom: "15px" }}>
                    <strong>{file.filename}</strong>
                    <pre
                      style={{
                        background: "#f6f8fa",
                        padding: "10px",
                        overflowX: "auto",
                        whiteSpace: "pre-wrap",
                        fontSize: "0.9em"
                      }}
                    >
                      {file.patch || "No diff available"}
                    </pre>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
