import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const CONTEXT_LINES = 20;

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [expandedHunks, setExpandedHunks] = useState({});

  // ------------------ Fetch PR details ------------------
  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  // ------------------ Parse patch into hunks ------------------
  const parseHunks = (patch) => {
    const lines = patch.split("\n");
    const hunks = [];
    let currentHunk = null;
    let oldLine = 0,
      newLine = 0;

    lines.forEach((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
        if (currentHunk) hunks.push(currentHunk);
        currentHunk = { header: line, lines: [] };
      } else if (currentHunk) {
        let type = "unchanged";
        let displayOld = oldLine + 1;
        let displayNew = newLine + 1;

        if (line.startsWith("+")) {
          type = "added";
          displayOld = "";
          newLine++;
        } else if (line.startsWith("-")) {
          type = "removed";
          displayNew = "";
          oldLine++;
        } else {
          oldLine++;
          newLine++;
        }

        currentHunk.lines.push({ line, type, oldLine: displayOld, newLine: displayNew });
      }
    });

    if (currentHunk) hunks.push(currentHunk);
    return hunks;
  };

  // ------------------ Expand/Collapse Logic ------------------
  const toggleExpand = (file, hIdx, direction, expand = true) => {
    setExpandedHunks((prev) => {
      const key = `${file.filename}-${hIdx}`;
      const state = prev[key] || { above: 0, below: 0 };
      const hunks = parseHunks(file.patch || "");
      const hunk = hunks[hIdx];
      if (!hunk) return prev;

      const changedLines = hunk.lines.filter((l) => l.type !== "unchanged");
      const firstChanged = changedLines.length
        ? changedLines[0].newLine
          ? changedLines[0].newLine - 1
          : changedLines[0].oldLine - 1
        : 0;
      const lastChanged = changedLines.length
        ? changedLines[changedLines.length - 1].newLine
          ? changedLines[changedLines.length - 1].newLine - 1
          : changedLines[changedLines.length - 1].oldLine - 1
        : 0;

      const newState = { ...state };
      const fullLength = file.fullContent.length;

      if (direction === "above") {
        newState.above = expand ? Math.min(state.above + CONTEXT_LINES, firstChanged) : 0;
      } else {
        newState.below = expand ? Math.min(state.below + CONTEXT_LINES, fullLength - 1 - lastChanged) : 0;
      }

      return { ...prev, [key]: newState };
    });
  };

  const expandAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 1000, below: 1000 };
    });
    setExpandedHunks(updated);
  };

  const collapseAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 0, below: 0 };
    });
    setExpandedHunks(updated);
  };

  // ------------------ Initialize hunks as fully expanded ------------------
  useEffect(() => {
    if (!prDetails) return;

    const initialExpanded = {};
    prDetails.files.forEach((file) => {
      const hunks = parseHunks(file.patch || "");
      hunks.forEach((_, hIdx) => {
        initialExpanded[`${file.filename}-${hIdx}`] = { above: 1000, below: 1000 };
      });
    });

    setExpandedHunks(initialExpanded);
  }, [prDetails]);

  // ------------------ Render ------------------
  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  const { details, files } = prDetails;

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{details.number}: <span className="text-primary">{details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <hr />
      <h4>Files Changed</h4>

      {files.map((file, fIdx) => {
        const hunks = parseHunks(file.patch || "");

        return (
          <details key={fIdx} open className="mb-3">
            <summary
              style={{
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.1rem",
                padding: "5px 10px",
                backgroundColor: "#f0f0f0",
                borderRadius: "5px",
              }}
            >
              {file.filename}
            </summary>

            <div style={{ margin: "10px 0", display: "flex", gap: "10px" }}>
              <button className="btn btn-sm btn-primary" onClick={() => expandAll(file)}>
                Expand All
              </button>
              <button className="btn btn-sm btn-secondary" onClick={() => collapseAll(file)}>
                Collapse All
              </button>
            </div>

            <div
              style={{
                maxHeight: "400px",
                overflow: "auto",
                border: "1px solid #ccc",
                borderRadius: "5px",
                marginTop: "5px",
                padding: "10px",
                backgroundColor: "#f6f8fa",
                fontFamily: "monospace",
              }}
            >
              {hunks.map((hunk, hIdx) => {
                const key = `${file.filename}-${hIdx}`;
                const state = expandedHunks[key] || { above: 0, below: 0 };

                const changedLines = hunk.lines.filter((l) => l.type !== "unchanged");
                const firstChanged = changedLines.length
                  ? changedLines[0].newLine
                    ? changedLines[0].newLine - 1
                    : changedLines[0].oldLine - 1
                  : 0;
                const lastChanged = changedLines.length
                  ? changedLines[changedLines.length - 1].newLine
                    ? changedLines[changedLines.length - 1].newLine - 1
                    : changedLines[changedLines.length - 1].oldLine - 1
                  : 0;

                const start = Math.max(0, firstChanged - state.above);
                const end = Math.min(file.fullContent.length - 1, lastChanged + state.below);

                const visibleLines = [];
                for (let i = start; i <= end; i++) {
                  const patchLine = hunk.lines.find(
                    (l) => (l.oldLine && l.oldLine - 1 === i) || (l.newLine && l.newLine - 1 === i)
                  );
                  visibleLines.push(
                    patchLine || { line: file.fullContent[i], type: "unchanged", oldLine: i + 1, newLine: i + 1 }
                  );
                }

                return (
                  <div key={hIdx} style={{ marginBottom: "15px" }}>
                    <div style={{ fontWeight: "bold", color: "#555" }}>{hunk.header}</div>

                    <div style={{ marginBottom: "5px" }}>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "above", true)}
                      >
                        ⬆ Expand above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "above", false)}
                      >
                        ⬆ Collapse above
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary me-2"
                        onClick={() => toggleExpand(file, hIdx, "below", true)}
                      >
                        ⬇ Expand below
                      </button>
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => toggleExpand(file, hIdx, "below", false)}
                      >
                        ⬇ Collapse below
                      </button>
                    </div>

                    {start > 0 && state.above < firstChanged && (
                      <div style={{ color: "#888" }}>... {start} hidden line(s) above ...</div>
                    )}

                    {visibleLines.map((lineObj, i) => {
                      let style = {};
                      if (lineObj.type === "added") style = { backgroundColor: "#d4f8d4" };
                      if (lineObj.type === "removed") style = { backgroundColor: "#f8d4d4" };
                      return (
                        <div key={i} style={style}>
                          <span
                            style={{
                              display: "inline-block",
                              width: "50px",
                              textAlign: "right",
                              paddingRight: "10px",
                              color: "#888",
                            }}
                          >
                            {lineObj.oldLine || ""}
                          </span>
                          <span
                            style={{
                              display: "inline-block",
                              width: "50px",
                              textAlign: "right",
                              paddingRight: "10px",
                              color: "#888",
                            }}
                          >
                            {lineObj.newLine || ""}
                          </span>
                          <span>{lineObj.line}</span>
                        </div>
                      );
                    })}

                    {end < file.fullContent.length - 1 && state.below < file.fullContent.length - lastChanged - 1 && (
                      <div style={{ color: "#888" }}>... {file.fullContent.length - 1 - end} hidden line(s) below ...</div>
                    )}
                  </div>
                );
              })}
            </div>
          </details>
        );
      })}
    </div>
  );
}
