import React, { useEffect, useRef } from "react";
import "../styles/Home.css"; // Ensure this path is correct

const Home = () => {
  const gradientRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!gradientRef.current) return;

      const { innerWidth, innerHeight } = window;
      const x = (e.clientX / innerWidth) * 100; // % across screen
      const y = (e.clientY / innerHeight) * 100; // % down screen

      // Gradient follows mouse movement
      gradientRef.current.style.backgroundPosition = `${x}% ${y}%`;
    };

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  return (
    <div
      ref={gradientRef}
      className="interactive-gradient flex flex-col items-center justify-center h-screen text-center px-6 font-sans"
    >
      {/* Heading */}
      <h1 className="text-4xl md:text-5xl font-bold text-white mb-4 tracking-tight">
        Welcome to <span className="text-indigo-400">GitMind</span>
      </h1>

      {/* Subheading */}
      <p className="text-lg text-gray-200 mb-8">
        Click below to continue with GitHub
      </p>

      {/* GitHub Login Button */}
      <a href="http://localhost:5000/auth/github">
        <button className="px-8 py-3 text-lg font-medium rounded-xl bg-indigo-600 text-white shadow-md hover:bg-indigo-700 hover:shadow-lg transition duration-300">
          Login with GitHub
        </button>
      </a>
    </div>
  );
};

export default Home;
