import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/Dashboard.css"; // <- Make sure this contains your .animated-gradient & .interactive-gradient

export default function Dashboard() {
  const { logout } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [cardsLoaded, setCardsLoaded] = useState(false);
  const navigate = useNavigate();

  // Fetch profile & repos
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      setError("No authentication token found.");
      setLoading(false);
      return;
    }
    const gradientRef = useRef(null);
    
      useEffect(() => {
        const handleMouseMove = (e) => {
          if (!gradientRef.current) return;
    
          const { innerWidth, innerHeight } = window;
          const x = (e.clientX / innerWidth) * 100; // % across screen
          const y = (e.clientY / innerHeight) * 100; // % down screen
    
          // Gradient follows mouse movement
          gradientRef.current.style.backgroundPosition = `${x}% ${y}%`;
        };
    
        window.addEventListener("mousemove", handleMouseMove);
        return () => window.removeEventListener("mousemove", handleMouseMove);
      }, []);
    

    const fetchData = async () => {
      try {
        const profileRes = await fetch("http://localhost:5001/github/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!profileRes.ok) throw new Error("Failed to fetch profile");
        const profileData = await profileRes.json();
        setProfile(profileData);

        const reposRes = await fetch("http://localhost:5001/github/repos", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!reposRes.ok) throw new Error("Failed to fetch repos");
        const reposData = await reposRes.json();
        setRepos(reposData);

        setCardsLoaded(true);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Interactive gradient background
  useEffect(() => {
    const dashboard = document.getElementById("dashboard-root");

    const onMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      dashboard.style.backgroundPosition = `${x}% ${y}%`;
    };

    window.addEventListener("mousemove", onMove);
    return () => window.removeEventListener("mousemove", onMove);
  }, []);

  if (loading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <p className="text-indigo-200 text-lg animate-pulse">Loading dashboard…</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="px-5 py-4 rounded-2xl backdrop-blur bg-white/5 shadow-xl">
          <p className="text-red-300 font-semibold">⚠️ {error}</p>
          <button
            onClick={logout}
            className="mt-4 px-4 py-2 rounded-xl bg-red-600 hover:bg-red-700 text-white shadow-md"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div
      id="dashboard-root"
      className="min-h-screen text-white px-5 md:px-8 py-8 animated-gradient interactive-gradient"
    >
      {/* Top bar */}
      <div className="flex items-center justify-between mb-8">
        <h2 className="text-2xl md:text-3xl font-bold tracking-tight">
          <span className="text-indigo-200">Repositories</span>
        </h2>
        <button
          onClick={() => {
            localStorage.removeItem("authToken");
            logout();
          }}
          className="px-4 py-2 rounded-xl bg-rose-500 hover:bg-rose-600 text-white shadow-md"
        >
          Logout
        </button>
      </div>

      {/* Profile */}
      {profile && (
        <div className="flex flex-col sm:flex-row items-start sm:items-center gap-5 mb-10">
          <img
            src={profile.avatar_url}
            alt="avatar"
            className="w-16 h-16 rounded-full ring-2 ring-white/40 shadow-md"
          />
          <div className="space-y-1">
            <div className="flex items-center gap-3">
              <h3 className="text-xl md:text-2xl font-semibold">
                {profile.name || profile.login}
              </h3>
              {profile.login && (
                <a
                  href={profile.html_url}
                  target="_blank"
                  rel="noreferrer"
                  className="text-sm text-indigo-200/90 hover:underline"
                >
                  @{profile.login}
                </a>
              )}
            </div>
            <p className="text-sm text-indigo-100/90">
              {profile.bio || "No bio available"}
            </p>
            <div className="flex flex-wrap gap-4 text-sm text-indigo-100/80">
              {profile.location && <span>📍 {profile.location}</span>}
              <span>👥 {profile.followers} followers</span>
              <span>🔗 {profile.following} following</span>
              <span>📦 {profile.public_repos} public repos</span>
            </div>
          </div>
        </div>
      )}

      {/* Repo grid */}
      {repos.length === 0 ? (
        <p className="text-indigo-100/80">No repositories found.</p>
      ) : (
        <div
          className={`grid gap-6 sm:gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 transition-opacity duration-700 ${
            cardsLoaded ? "opacity-100" : "opacity-0"
          } m-[10%]`}
        >
          {repos.map((repo, i) => (
            <div
              key={repo.id}
              className="rounded-2xl p-5 md:p-6 bg-white/5 shadow-[0_10px_35px_rgba(0,0,0,0.35)] hover:shadow-[0_14px_40px_rgba(0,0,0,0.45)] transition-transform duration-300 hover:-translate-y-1"
              style={{
                animation: `fadeInUp 0.6s ease forwards`,
                animationDelay: `${i * 90}ms`,
              }}
            >
              <h3 className="text-lg md:text-xl font-semibold text-indigo-100">
                {repo.name}
              </h3>
              <p className="text-sm text-indigo-100/85 mt-2 line-clamp-2">
                {repo.description || "No description provided"}
              </p>

              <div className="flex gap-5 mt-4 text-xs text-indigo-100/70">
                <span>⭐ {repo.stargazers_count}</span>
                <span>🍴 {repo.forks_count}</span>
                <span>👀 {repo.watchers_count}</span>
              </div>

              <div className="mt-5 flex gap-3">
                <button
                  onClick={() => window.open(repo.html_url, "_blank")}
                  className="px-3 py-2 rounded-xl text-sm bg-indigo-500 hover:bg-indigo-600 text-white shadow-md"
                >
                  View Repo
                </button>
                <button
                  onClick={() =>
                    navigate(`/repos/${repo.owner.login}/${repo.name}/prs`)
                  }
                  className="px-3 py-2 rounded-xl text-sm bg-emerald-500 hover:bg-emerald-600 text-white shadow-md"
                >
                  View PRs
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(10px); }
            100% { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
