import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function PRList() {
  const { owner, repo } = useParams();
  const [prs, setPRs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRs = async () => {
      try {
        const token = localStorage.getItem("authToken");
        const res = await fetch(`http://localhost:5001/github/repos/${owner}/${repo}/prs`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error(`Failed to fetch PRs: ${res.status}`);
        const data = await res.json();
        setPRs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPRs();
  }, [owner, repo]);

  if (loading) return <p className="p-4">Loading PRs...</p>;
  if (error) return <p className="p-4 text-danger">Error: {error}</p>;

  return (
    <div className="container py-4">
      <h2>Pull Requests for {owner}/{repo}</h2>
      {prs.length === 0 ? (
        <p>No pull requests found.</p>
      ) : (
        <ul className="list-group">
          {prs.map((pr) => (
            <li key={pr.id} className="list-group-item">
              <a href={pr.html_url} target="_blank" rel="noopener noreferrer">
                #{pr.number} {pr.title}
              </a>
              <span className={`badge ms-2 ${pr.state === "open" ? "bg-success" : "bg-secondary"}`}>
                {pr.state}
              </span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
