import React, { useEffect, useState, useRef } from "react";
import { useParams, Link } from "react-router-dom";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const fileRefs = useRef({});

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();

        if (!data.details) data.details = {};
        if (!data.files) data.files = [];

        // For each file, fetch full content
        const filesWithContent = await Promise.all(
          data.files.map(async (file) => {
            if (!file.sha) return { ...file, fullContent: [] };

            const contentRes = await fetch(
              `http://localhost:5001/github/repos/${owner}/${repo}/contents/${file.filename}?ref=${data.details.head?.sha}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            if (!contentRes.ok) return { ...file, fullContent: [] };
            const contentData = await contentRes.json();
            const contentLines = atob(contentData.content).split("\n");

            return { ...file, fullContent: contentLines };
          })
        );

        setPrDetails({ ...data, files: filesWithContent });
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  useEffect(() => {
    if (prDetails?.files) {
      prDetails.files.forEach((file) => {
        const container = fileRefs.current[file.filename];
        if (container) {
          const lines = container.querySelectorAll("div");
          for (let line of lines) {
            if (line.style.backgroundColor) {
              container.scrollTop = line.offsetTop - container.offsetTop - 10;
              break;
            }
          }
        }
      });
    }
  }, [prDetails]);

  const highlightLines = (fullContent, patch) => {
    const patchLines = patch ? patch.split("\n") : [];
    const changesMap = {}; // map line number => type

    let oldLine = 0;
    let newLine = 0;

    patchLines.forEach((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
      } else if (line.startsWith("+")) {
        changesMap[newLine] = "added";
        newLine++;
      } else if (line.startsWith("-")) {
        changesMap[oldLine] = "removed";
        oldLine++;
      } else {
        oldLine++;
        newLine++;
      }
    });

    return fullContent.map((lineText, idx) => ({
      text: lineText,
      type: changesMap[idx] || "unchanged",
      oldLine: idx + 1,
      newLine: idx + 1,
    }));
  };

  if (loading) return <p className="text-center mt-5">Loading PR details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;
  if (!prDetails) return <p className="text-center mt-5">No details found for this PR.</p>;

  const { details, files } = prDetails;

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{details.number}: <span className="text-primary">{details.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          â¬… Back to PR List
        </Link>
      </div>

      <p><strong>Author:</strong> {details.user?.login || "N/A"}</p>
      <p><strong>State:</strong> {details.state || "N/A"}</p>
      <p><strong>Created At:</strong> {details.created_at ? new Date(details.created_at).toLocaleString() : "N/A"}</p>

      <hr />
      <h4>Description</h4>
      <p>{details.body || "No description provided."}</p>

      <hr />
      <h4>Files Changed</h4>
      {files.length === 0 ? (
        <p>No files changed.</p>
      ) : (
        files.map((file, idx) => (
          <details key={idx} open className="mb-3">
            <summary
              style={{
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1.1rem",
                padding: "5px 10px",
                backgroundColor: "#f0f0f0",
                borderRadius: "5px",
              }}
            >
              {file.filename}
            </summary>
            <div
              ref={(el) => (fileRefs.current[file.filename] = el)}
              style={{
                maxHeight: "500px",
                overflow: "auto",
                border: "1px solid #ccc",
                borderRadius: "5px",
                marginTop: "5px",
                padding: "10px",
                backgroundColor: "#f6f8fa",
                fontFamily: "monospace",
              }}
            >
              {file.fullContent && file.fullContent.length > 0 ? (
                highlightLines(file.fullContent, file.patch).map((lineObj, i) => {
                  let style = {};
                  if (lineObj.type === "added") style = { backgroundColor: "#d4f8d4" };
                  if (lineObj.type === "removed") style = { backgroundColor: "#f8d4d4" };
                  return (
                    <div key={i} style={style}>
                      <span style={{ display: "inline-block", width: "50px", textAlign: "right", paddingRight: "10px", color: "#888" }}>
                        {lineObj.oldLine}
                      </span>
                      <span style={{ display: "inline-block", width: "50px", textAlign: "right", paddingRight: "10px", color: "#888" }}>
                        {lineObj.newLine}
                      </span>
                      <span>{lineObj.text}</span>
                    </div>
                  );
                })
              ) : (
                <p>No content available.</p>
              )}
            </div>
          </details>
        ))
      )}
    </div>
  );
}
