import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { parseDiff, Diff, Hunk } from "react-diff-view";
import "react-diff-view/style/index.css";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [fileDiffs, setFileDiffs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRData = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        // 1. Fetch PR details
        const detailsRes = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!detailsRes.ok) throw new Error("Failed to fetch PR details");
        const detailsData = await detailsRes.json();
        setPrDetails(detailsData.details || detailsData);

        // 2. Fetch PR file changes
        const filesRes = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/files`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!filesRes.ok) throw new Error("Failed to fetch PR files");
        const filesData = await filesRes.json();

        // 3. Prepare and parse diffs
        const diffText = filesData
          .filter(f => f.patch)
          .map(f => `diff --git a/${f.filename} b/${f.filename}\n${f.patch}`)
          .join("\n");

        const parsedDiffs = parseDiff(diffText);
        setFileDiffs(parsedDiffs);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRData();
  }, [owner, repo, number]);

  if (loading) return <p>Loading PR details...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container py-4">
      {/* PR Metadata */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR #{prDetails.number}:{" "}
          <span className="text-primary">{prDetails.title}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back to PR List
        </Link>
      </div>

      <p>
        <strong>Author:</strong> {prDetails.user?.login || "N/A"}
      </p>
      <p>
        <strong>State:</strong>{" "}
        <span
          className={`badge ${
            prDetails.state === "open" ? "bg-success" : "bg-secondary"
          }`}
        >
          {prDetails.state?.toUpperCase()}
        </span>
      </p>
      <hr />

      {/* PR File Changes */}
      <h3>Files Changed</h3>
      {fileDiffs.length === 0 && <p>No file changes found.</p>}
      {fileDiffs.map(file => (
        <div key={file.newPath} style={{ marginBottom: "2rem" }}>
          <h5>{file.newPath}</h5>
          <Diff viewType="split" diffType={file.type} hunks={file.hunks}>
            {hunks => hunks.map(hunk => <Hunk key={hunk.content} hunk={hunk} />)}
          </Diff>
        </div>
      ))}
    </div>
  );
}
