import React from "react";
import { motion } from "framer-motion";
import { Mail, Phone, MapPin, Send } from "lucide-react";
import profileImg from "../assets/profile.JPG";

export default function ContactUs() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-indigo-700 to-black flex items-center justify-center p-6 overflow-y-auto">
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ type: "spring", stiffness: 120, damping: 12 }}
        className="w-full max-w-4xl bg-white/10 backdrop-blur-md border border-white/20 p-8 rounded-2xl shadow-2xl flex flex-col md:flex-row gap-8"
      >
        {/* Profile Card */}
        <motion.div
          whileHover={{ scale: 1.05, rotate: 2 }}
          transition={{ type: "spring", stiffness: 300 }}
          className="flex flex-col items-center text-center bg-white/20 rounded-2xl p-6 shadow-lg md:w-1/3"
        >
          <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-white shadow-lg mb-4">
            <img
              src={profileImg}
              alt="Profile"
              className="w-full h-full object-cover"
            />
          </div>
          <h2 className="text-xl font-bold text-white">Your Name</h2>
          <p className="text-sm text-gray-200">Full Stack Developer</p>
        </motion.div>

        {/* Contact Form */}
        <motion.div
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ type: "spring", stiffness: 100 }}
          className="flex-1"
        >
          <h2 className="text-2xl font-bold text-white mb-4">Contact Me</h2>
          <form className="flex flex-col gap-4">
            <input
              type="text"
              placeholder="Your Name"
              className="p-3 rounded-xl bg-white/20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            />
            <input
              type="email"
              placeholder="Your Email"
              className="p-3 rounded-xl bg-white/20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            />
            <textarea
              placeholder="Your Message"
              rows="5"
              className="p-3 rounded-xl bg-white/20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 transition"
            ></textarea>
            <motion.button
              whileTap={{ scale: 0.95 }}
              whileHover={{ scale: 1.05 }}
              className="flex items-center justify-center gap-2 bg-gradient-to-r from-pink-500 to-purple-600 text-white font-semibold py-3 rounded-xl shadow-lg hover:shadow-2xl transition"
            >
              <Send size={18} /> Send Message
            </motion.button>
          </form>

          {/* Contact Info */}
          <div className="mt-6 space-y-3 text-gray-200">
            <p className="flex items-center gap-2"><Mail size={18}/> youremail@example.com</p>
            <p className="flex items-center gap-2"><Phone size={18}/> +91 9876543210</p>
            <p className="flex items-center gap-2"><MapPin size={18}/> Bangalore, India</p>
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
}
