import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

const CONTEXT_LINES = 20;

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [expandedHunks, setExpandedHunks] = useState({});
  const [analysis, setAnalysis] = useState(null);
  const [analyzing, setAnalyzing] = useState(false);

  // Fetch PR Details
  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to fetch PR details: ${res.status}`);
        const data = await res.json();
        setPrDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  // Fetch Analysis
  useEffect(() => {
    const fetchAnalysis = async () => {
      setAnalyzing(true);
      setAnalysis(null);
      try {
        const token = localStorage.getItem("authToken");
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/analyze`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error(`Failed to analyze PR: ${res.status}`);
        const data = await res.json();
        setAnalysis(data.analysis || { summary: "No issues detected âœ…", findings: [] });
      } catch (err) {
        setError(err.message);
      } finally {
        setAnalyzing(false);
      }
    };

    fetchAnalysis();
  }, [owner, repo, number]);

  if (loading)
    return (
      <div className="flex items-center justify-center min-h-screen text-white font-sans">
        <div className="w-12 h-12 border-4 border-indigo-500 border-t-transparent rounded-full animate-spin"></div>
        <p className="ml-4 text-indigo-400">Loading PR details...</p>
      </div>
    );

  if (error)
    return (
      <div className="flex items-center justify-center min-h-screen text-red-400 font-sans">
        <p className="text-lg font-medium">{error}</p>
      </div>
    );

  if (!prDetails) return <p className="text-center mt-5 text-white">No PR details found</p>;

  const { details, files } = prDetails;

  // --- Hunk Parsing & Expand Logic ---
  const parseHunks = (patch) => {
    const lines = patch.split("\n");
    const hunks = [];
    let currentHunk = null;
    let oldLine = 0,
      newLine = 0;

    lines.forEach((line) => {
      if (line.startsWith("@@")) {
        const match = /@@ -(\d+),?\d* \+(\d+),?\d* @@/.exec(line);
        if (match) {
          oldLine = parseInt(match[1], 10) - 1;
          newLine = parseInt(match[2], 10) - 1;
        }
        if (currentHunk) hunks.push(currentHunk);
        currentHunk = { header: line, lines: [] };
      } else if (currentHunk) {
        let type = "unchanged";
        let displayOld = oldLine + 1;
        let displayNew = newLine + 1;

        if (line.startsWith("+")) {
          type = "added";
          displayOld = "";
          newLine++;
        } else if (line.startsWith("-")) {
          type = "removed";
          displayNew = "";
          oldLine++;
        } else {
          oldLine++;
          newLine++;
        }

        currentHunk.lines.push({ line, type, oldLine: displayOld, newLine: displayNew });
      }
    });

    if (currentHunk) hunks.push(currentHunk);
    return hunks;
  };

  const toggleExpand = (file, hIdx, direction, expand = true) => {
    setExpandedHunks((prev) => {
      const key = `${file.filename}-${hIdx}`;
      const state = prev[key] || { above: 0, below: 0 };
      const hunks = parseHunks(file.patch || "");
      const hunk = hunks[hIdx];
      if (!hunk) return prev;

      const firstChanged = hunk.lines.findIndex((l) => l.type !== "unchanged");
      const lastChanged =
        hunk.lines.length - 1 - [...hunk.lines].reverse().findIndex((l) => l.type !== "unchanged");

      const newState = { ...state };
      if (direction === "above") {
        newState.above = expand ? Math.min(state.above + CONTEXT_LINES, firstChanged) : 0;
      } else {
        newState.below = expand ? Math.min(state.below + CONTEXT_LINES, hunk.lines.length - 1 - lastChanged) : 0;
      }

      return { ...prev, [key]: newState };
    });
  };

  const expandAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 1000, below: 1000 };
    });
    setExpandedHunks(updated);
  };

  const collapseAll = (file) => {
    const updated = {};
    parseHunks(file.patch || "").forEach((_, hIdx) => {
      updated[`${file.filename}-${hIdx}`] = { above: 0, below: 0 };
    });
    setExpandedHunks(updated);
  };

  return (
    <div className="min-h-screen px-6 py-10 text-white font-sans bg-gradient-to-br from-[#0a1a66] via-[#0d1b4d] to-[#020833]">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-10">
        <h2 className="text-3xl font-bold tracking-wide">
          PR #{details.number}: <span className="text-indigo-300">{details.title}</span>
        </h2>
        <Link
          to={`/repos/${owner}/${repo}/prs`}
          className="mt-4 md:mt-0 px-5 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
        >
          â¬… Back to PR List
        </Link>
      </div>

      <div className="flex flex-col lg:flex-row gap-8">
        {/* Analysis */}
        <div className="lg:w-1/3 bg-[#11142a] p-4 rounded-xl shadow-md flex flex-col">
          <h3 className="text-xl font-semibold mb-3 text-indigo-300">Rule Engine Analysis</h3>
          <div className="overflow-y-auto max-h-[500px]">
            {analyzing && <p className="text-gray-300">Analyzing PR...</p>}
            {!analyzing && analysis && (
              <>
                <p className="mb-2 font-semibold">{analysis.summary}</p>
                {analysis.findings && analysis.findings.length > 0 ? (
                  <ul className="space-y-2">
                    {analysis.findings.map((result, idx) => {
                      let color = "text-blue-400";
                      if (result.severity === "warning") color = "text-yellow-400";
                      if (result.severity === "critical") color = "text-red-500";
                      if (result.severity === "safe") color = "text-green-400";

                      return (
                        <li key={idx}>
                          <span className={`font-bold ${color}`}>[{result.severity.toUpperCase()}]</span>{" "}
                          <strong>{result.file}</strong>: {result.message || ""}
                        </li>
                      );
                    })}
                  </ul>
                ) : (
                  <p className="text-green-400">âœ… No issues detected</p>
                )}
              </>
            )}
          </div>
        </div>

        {/* Files Changed */}
        <div className="lg:w-2/3 flex flex-col gap-6">
          {files.map((file, fIdx) => {
            const hunks = parseHunks(file.patch || "");
            return (
              <div key={fIdx} className="bg-[#11142a] p-4 rounded-xl shadow-md">
                <div className="flex justify-between items-center mb-3">
                  <h4 className="font-semibold text-indigo-300">{file.filename}</h4>
                  <div className="flex gap-2">
                    <button onClick={() => expandAll(file)} className="px-2 py-1 text-xs bg-indigo-600/30 rounded hover:bg-indigo-600/50">Expand All</button>
                    <button onClick={() => collapseAll(file)} className="px-2 py-1 text-xs bg-gray-600/30 rounded hover:bg-gray-600/50">Collapse All</button>
                  </div>
                </div>

                <div className="overflow-auto max-h-[400px] bg-[#0b0d2a] rounded p-2 font-mono text-sm">
                  {hunks.map((hunk, hIdx) => {
                    const key = `${file.filename}-${hIdx}`;
                    const state = expandedHunks[key] || { above: 0, below: 0 };
                    const firstChanged = hunk.lines.findIndex((l) => l.type !== "unchanged");
                    const lastChanged =
                      hunk.lines.length - 1 - [...hunk.lines].reverse().findIndex((l) => l.type !== "unchanged");
                    const start = Math.max(0, firstChanged - state.above);
                    const end = Math.min(hunk.lines.length - 1, lastChanged + state.below);
                    const visibleLines = hunk.lines.slice(start, end + 1);

                    return (
                      <div key={hIdx} className="mb-3">
                        <div className="text-gray-500 font-bold">{hunk.header}</div>
                        <div className="flex flex-wrap gap-1 mb-1">
                          <button onClick={() => toggleExpand(file, hIdx, "above", true)} className="px-1 text-xs bg-indigo-600/30 rounded hover:bg-indigo-600/50">â¬† Expand above</button>
                          <button onClick={() => toggleExpand(file, hIdx, "above", false)} className="px-1 text-xs bg-gray-600/30 rounded hover:bg-gray-600/50">â¬† Collapse above</button>
                          <button onClick={() => toggleExpand(file, hIdx, "below", true)} className="px-1 text-xs bg-indigo-600/30 rounded hover:bg-indigo-600/50">â¬‡ Expand below</button>
                          <button onClick={() => toggleExpand(file, hIdx, "below", false)} className="px-1 text-xs bg-gray-600/30 rounded hover:bg-gray-600/50">â¬‡ Collapse below</button>
                        </div>

                        {start > 0 && <div className="text-gray-400">... {start} hidden line(s) above ...</div>}

                        {visibleLines.map((lineObj, i) => {
                          let bg = "";
                          if (lineObj.type === "added") bg = "bg-green-600/20";
                          if (lineObj.type === "removed") bg = "bg-red-600/20";
                          return (
                            <div key={i} className={`flex`}>
                              <span className="w-12 text-right pr-2 text-gray-300">{lineObj.oldLine || ""}</span>
                              <span className="w-12 text-right pr-2 text-gray-300">{lineObj.newLine || ""}</span>
                              <span className={`flex-1 ${bg}`}>{lineObj.line}</span>
                            </div>
                          );
                        })}

                        {end < hunk.lines.length - 1 && <div className="text-gray-400">... {hunk.lines.length - 1 - end} hidden line(s) below ...</div>}
                      </div>
                    );
                  })}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* AI Summary */}
      {analysis && analysis.aiSummary && (
        <div className="mt-6 p-4 bg-[#11142a] rounded-xl shadow-md">
          <h4 className="font-semibold text-indigo-300 mb-2">ðŸ¤– AI Summary</h4>
          {analysis.aiSummary.split("\n").map((line, idx) => (
            <p key={idx} className="text-gray-200 mb-1">{line}</p>
          ))}
        </div>
      )}
    </div>
  );
}
