import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [pr, setPR] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRDetails = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls/${number}/details`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (!res.ok) {
          throw new Error(`Failed to fetch PR details: ${res.status}`);
        }

        const data = await res.json();
        setPR(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  if (loading) return <p className="text-center mt-5">Loading pull request details...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;

  if (!pr) {
    return (
      <p className="text-center text-warning mt-5">
        Pull Request not found.
      </p>
    );
  }

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>
          PR Details — <span className="text-primary">#{pr.number}</span>
        </h2>
        <Link to={`/repos/${owner}/${repo}/prs`} className="btn btn-secondary">
          ⬅ Back
        </Link>
      </div>

      <div className="card shadow-sm border-0">
        <div className="card-body">
          <h4 className="card-title">{pr.title}</h4>
          <p className="mb-1">
            <small className="text-muted">
              Created by <strong>{pr.user?.login}</strong> on{" "}
              {new Date(pr.created_at).toLocaleDateString()}
            </small>
          </p>
          <p>
            <span
              className={`badge ${
                pr.state === "open" ? "bg-success" : "bg-secondary"
              }`}
            >
              {pr.state?.toUpperCase()}
            </span>
          </p>
          <p>{pr.body || "No description provided."}</p>
          <a
            href={pr.html_url}
            target="_blank"
            rel="noopener noreferrer"
            className="btn btn-primary"
          >
            View on GitHub
          </a>
        </div>
      </div>
    </div>
  );
}
