import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export default function PRList() {
  const { owner, repo } = useParams();
  const [prs, setPRs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRs = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(`http://localhost:5001/github/repos/${owner}/${repo}/pulls`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error(`Failed to fetch PRs: ${res.status}`);
        }

        const data = await res.json();
        setPRs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRs();
  }, [owner, repo]);

  if (loading) return <p className="text-center mt-5">Loading pull requests...</p>;
  if (error) return <p className="text-center text-danger mt-5">{error}</p>;

  return (
    <div className="container py-4">
      <h2 className="mb-4">
        Pull Requests for <strong>{owner}/{repo}</strong>
      </h2>
      <Link to="/dashboard" className="btn btn-secondary mb-3">
        â¬… Back to Dashboard
      </Link>

      {prs.length === 0 ? (
        <p className="text-muted">No pull requests have been raised for this repository.</p>
      ) : (
        <div className="list-group">
          {prs.map((pr) => (
            <a
              key={pr.id}
              href={pr.html_url}
              target="_blank"
              rel="noopener noreferrer"
              className="list-group-item list-group-item-action d-flex justify-content-between align-items-center"
            >
              <div>
                <h5 className="mb-1">{pr.title}</h5>
                <small className="text-muted">
                  #{pr.number} opened by {pr.user.login} on {new Date(pr.created_at).toLocaleDateString()}
                </small>
              </div>
              <span
                className={`badge ${
                  pr.state === "open" ? "bg-success" : "bg-secondary"
                }`}
              >
                {pr.state.toUpperCase()}
              </span>
            </a>
          ))}
        </div>
      )}
    </div>
  );
}
