import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const { logout } = useContext(AuthContext);
  const [repos, setRepos] = useState([]);
  const [profile, setProfile] = useState(null);
  const [pullRequests, setPullRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [cardsLoaded, setCardsLoaded] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      setError("No authentication token found.");
      setLoading(false);
      return;
    }

    const fetchData = async () => {
      try {
        // Profile
        const profileRes = await fetch("http://localhost:5001/github/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!profileRes.ok) throw new Error("Failed to fetch profile");
        const profileData = await profileRes.json();
        setProfile(profileData);

        // Repos
        const reposRes = await fetch("http://localhost:5001/github/repos", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!reposRes.ok) throw new Error("Failed to fetch repos");
        const reposData = await reposRes.json();
        setRepos(reposData);

        // Pull Requests (new endpoint assumed)
        const prsRes = await fetch("http://localhost:5001/github/pull-requests", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!prsRes.ok) throw new Error("Failed to fetch pull requests");
        const prsData = await prsRes.json();
        setPullRequests(prsData);

        setCardsLoaded(true);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="flex h-screen items-center justify-center bg-gradient-to-br from-gray-900 via-gray-800 to-black">
        <p className="text-indigo-400 text-lg animate-pulse">
          Loading dashboard...
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex h-screen items-center justify-center bg-gray-900">
        <div className="p-6 bg-gray-800 rounded-xl shadow-lg border border-red-500/50 text-red-400">
          <p className="text-lg font-semibold">⚠️ Error</p>
          <p className="text-sm">{error}</p>
          <button
            onClick={logout}
            className="mt-4 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg shadow-md"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white p-6">
      <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
        {/* Left Column: Pull Requests */}
        <div className="lg:col-span-1 bg-gray-800/60 rounded-xl p-4 border border-indigo-700/50 shadow-md">
          <h2 className="text-xl font-bold text-indigo-400 mb-4">
            Open Pull Requests
          </h2>
          {pullRequests.length === 0 ? (
            <p className="text-gray-400 text-sm">No open PRs.</p>
          ) : (
            <div className="max-h-[500px] overflow-y-auto space-y-3 pr-2 scrollbar-thin scrollbar-thumb-indigo-600/40">
              {pullRequests.map((pr) => (
                <div
                  key={pr.id}
                  onClick={() => window.open(pr.html_url, "_blank")}
                  className="cursor-pointer p-3 bg-gray-900/50 rounded-lg border border-gray-700 hover:bg-gray-700/60 transition"
                >
                  <p className="text-indigo-300 text-sm font-medium line-clamp-1">
                    {pr.title}
                  </p>
                  <p className="text-gray-400 text-xs mt-1">
                    #{pr.number} • {pr.repository?.name || "Repo"}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Right Column: Profile + Repositories */}
        <div className="lg:col-span-3 space-y-10">
          {/* Profile Card */}
          {profile && (
            <div className="p-6 rounded-xl bg-gradient-to-r from-indigo-800/60 via-indigo-900/70 to-indigo-950/80 border border-indigo-700/50 shadow-lg flex flex-col sm:flex-row items-center gap-6">
              <img
                src={profile.avatar_url}
                alt="avatar"
                className="w-20 h-20 rounded-full border-2 border-indigo-400 shadow-md"
              />
              <div>
                <h3 className="text-2xl font-bold text-white">
                  {profile.name || profile.login}
                </h3>
                <p className="text-gray-300 text-sm mt-1">
                  {profile.bio || "No bio available"}
                </p>
                <div className="flex gap-6 mt-3 text-gray-400 text-sm">
                  <span>👥 Followers: {profile.followers}</span>
                  <span>🔗 Following: {profile.following}</span>
                  <span>📦 Public Repos: {profile.public_repos}</span>
                </div>
              </div>
            </div>
          )}

          {/* Repositories */}
          <h2 className="text-3xl font-bold text-indigo-400 mb-6">
            Your Repositories
          </h2>
          {repos.length === 0 ? (
            <p className="text-gray-400">No repositories found.</p>
          ) : (
            <div
              className={`grid gap-8 transition-opacity duration-1000 ${
                cardsLoaded ? "opacity-100" : "opacity-0"
              } grid-cols-1 sm:grid-cols-2 lg:grid-cols-3`}
            >
              {repos.map((repo, index) => (
                <div
                  key={repo.id}
                  className="p-5 rounded-xl bg-gradient-to-br from-indigo-800/40 via-indigo-900/50 to-indigo-950/60
                    border border-indigo-700/50 shadow-lg transform transition-all duration-500 hover:scale-105 hover:shadow-indigo-600/30"
                  style={{
                    animation: `fadeInUp 0.6s ease forwards`,
                    animationDelay: `${index * 0.15}s`,
                  }}
                >
                  <h3 className="text-lg font-semibold text-indigo-300">
                    {repo.name}
                  </h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">
                    {repo.description || "No description provided"}
                  </p>
                  <div className="flex gap-6 mt-3 text-gray-400 text-xs">
                    <span>⭐ {repo.stargazers_count}</span>
                    <span>🍴 {repo.forks_count}</span>
                    <span>👀 {repo.watchers_count}</span>
                  </div>

                  {/* Action buttons */}
                  <div className="mt-4 flex gap-2">
                    <button
                      onClick={() => window.open(repo.html_url, "_blank")}
                      className="px-3 py-1 text-xs bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg shadow-md"
                    >
                      View Repo
                    </button>
                    <button
                      onClick={() =>
                        navigate(`/repos/${repo.owner.login}/${repo.name}/prs`)
                      }
                      className="px-3 py-1 text-xs rounded-lg bg-gray-700/30 border border-gray-500 text-gray-300 
                        hover:bg-gray-600/50 hover:transition duration-300"
                    >
                      View PRs
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Keyframes */}
      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
