import React, { useContext, useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [avatarOpen, setAvatarOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setAvatarOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <nav className="w-full bg-black shadow-lg">
      <div className="max-w-7xl mx-auto flex justify-between items-center px-6 py-3">
        
        {/* Left section: Brand + Help/Contact */}
        <div className="flex items-center space-x-6">
          <Link
            to="/"
            className="text-2xl font-semibold text-indigo-400 hover:text-indigo-300 transition"
          >
            GitMind
          </Link>
          <Link
            to="/help"
            className="text-white hover:text-indigo-400 transition font-medium"
          >
            Help
          </Link>
          <Link
            to="/contact"
            className="text-white hover:text-indigo-400 transition font-medium"
          >
            Contact
          </Link>
        </div>

        {/* Right section: Nav links + Avatar */}
        <div className="flex items-center space-x-4">
          {user && (
            <>
              <Link
                to="/"
                className="text-white hover:text-indigo-400 transition font-medium"
              >
                Home
              </Link>
              <Link
                to="/dashboard"
                className="text-white hover:text-indigo-400 transition font-medium"
              >
                Dashboard
              </Link>

              {/* Avatar + Dropdown */}
              <div className="relative" ref={dropdownRef}>
                <img
                  src={user.avatar_url} // assuming user object has avatar_url
                  alt="avatar"
                  onClick={() => setAvatarOpen(!avatarOpen)}
                  className="w-10 h-10 rounded-full cursor-pointer border-2 border-white/20"
                />
                {avatarOpen && (
                  <div className="absolute right-0 mt-2 w-40 bg-white rounded-lg shadow-lg py-2 z-50">
                    <button
                      onClick={logout}
                      className="w-full text-left px-4 py-2 text-red-600 hover:bg-red-100 rounded-lg transition"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          )}

          {!user && (
            <Link
              to="/login"
              className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition"
            >
              Login
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
