import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function PRDetails() {
  const { owner, repo, number } = useParams();
  const [prData, setPrData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("jwtToken");

  useEffect(() => {
    const fetchPRDetails = async () => {
      try {
        setLoading(true);
        setError("");

        const res = await axios.get(
          `${process.env.REACT_APP_API_URL}/repos/${owner}/${repo}/pulls/${number}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setPrData(res.data);
      } catch (err) {
        console.error("Error fetching PR details:", err);
        setError("Failed to fetch PR details. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number, token]);

  const renderDiff = (patch) => {
    if (!patch) return <p className="text-gray-500">No diff available</p>;
    return patch.split("\n").map((line, idx) => {
      let bgClass = "";
      if (line.startsWith("+") && !line.startsWith("+++")) {
        bgClass = "bg-green-100 text-green-800";
      } else if (line.startsWith("-") && !line.startsWith("---")) {
        bgClass = "bg-red-100 text-red-800";
      } else {
        bgClass = "bg-gray-100 text-gray-800";
      }
      return (
        <div
          key={idx}
          className={`whitespace-pre-wrap font-mono text-sm px-2 py-0.5 ${bgClass}`}
        >
          {line}
        </div>
      );
    });
  };

  if (loading) return <p>Loading PR details...</p>;
  if (error) return <p className="text-red-500">{error}</p>;
  if (!prData) return <p>No PR data found.</p>;

  const { details, commits, files } = prData;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-2">{details?.title || "Untitled PR"}</h1>
      <p>Status: {details?.state}</p>
      <p>Author: {details?.user?.login}</p>
      <p>Created: {details?.created_at ? new Date(details.created_at).toLocaleString() : "N/A"}</p>

      {/* Commits */}
      <h2 className="text-lg font-semibold mt-6">Commits</h2>
      <ul className="list-disc pl-5 mb-4">
        {(commits || []).map((commit) => (
          <li key={commit.sha}>
            {commit.commit?.message} — <span className="italic">{commit.commit?.author?.name}</span>
          </li>
        ))}
      </ul>

      {/* File Changes */}
      <h2 className="text-lg font-semibold mt-6">Files Changed</h2>
      {(files || []).map((file) => (
        <div key={file.filename} className="mb-6 border rounded overflow-hidden">
          <div className="bg-gray-200 px-3 py-2 font-medium border-b">
            {file.filename}{" "}
            <span className="text-sm text-gray-600">
              (+{file.additions} / -{file.deletions})
            </span>
          </div>
          <div className="overflow-x-auto">{renderDiff(file.patch)}</div>
        </div>
      ))}
    </div>
  );
}
