import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { diffChars } from "diff";

const PRDetails = () => {
  const { owner, repo, number } = useParams();
  const [prDetails, setPrDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPRDetails = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_URL}/repos/${owner}/${repo}/pulls/${number}/details`
        );
        setPrDetails(res.data);
      } catch (err) {
        setError("Failed to load PR details");
      } finally {
        setLoading(false);
      }
    };

    fetchPRDetails();
  }, [owner, repo, number]);

  const renderDiff = (patch) => {
    if (!patch) return <p className="text-gray-500">No diff available</p>;

    return patch.split("\n").map((line, idx) => {
      if (line.startsWith("+++")) return null;
      if (line.startsWith("---")) return null;

      let lineClass = "";
      if (line.startsWith("+")) {
        lineClass = "bg-green-50";
      } else if (line.startsWith("-")) {
        lineClass = "bg-red-50";
      } else {
        lineClass = "bg-gray-50";
      }

      // For added/removed lines, highlight character-level changes
      let content = line;
      if (line.startsWith("+") || line.startsWith("-")) {
        const baseText = line.substring(1); // remove +/-
        const comparisonText = ""; // No direct old/new match, just highlight added chars
        const parts = diffChars(comparisonText, baseText);

        content = (
          <>
            {line[0]}
            {parts.map((part, i) => {
              let charClass = "";
              if (part.added) charClass = "bg-green-200";
              if (part.removed) charClass = "bg-red-200 line-through";
              return (
                <span key={i} className={`${charClass}`}>
                  {part.value}
                </span>
              );
            })}
          </>
        );
      }

      return (
        <div
          key={idx}
          className={`whitespace-pre font-mono text-sm px-2 py-0.5 ${lineClass}`}
        >
          {content}
        </div>
      );
    });
  };

  if (loading) return <p>Loading PR details...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="p-4">
      {prDetails && (
        <>
          <h1 className="text-xl font-bold mb-2">{prDetails.title}</h1>
          <p className="text-gray-600 mb-4">
            #{prDetails.number} opened by {prDetails.user.login}
          </p>

          {prDetails.files?.map((file) => (
            <div key={file.filename} className="mb-6">
              <h2 className="text-lg font-semibold mb-2">{file.filename}</h2>
              <div className="border rounded overflow-hidden">
                {renderDiff(file.patch)}
              </div>
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default PRDetails;
