import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";

export default function PRList() {
  const { owner, repo } = useParams();
  const [prs, setPRs] = useState([]);
  const [filteredPRs, setFilteredPRs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // Fetch PRs
  useEffect(() => {
    const fetchPRs = async () => {
      const token = localStorage.getItem("authToken");
      if (!token) {
        setError("No authentication token found.");
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5001/github/repos/${owner}/${repo}/pulls`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (!res.ok) throw new Error(`Failed to fetch PRs: ${res.status}`);

        const data = await res.json();

        // Initially sort by creation date descending
        const sorted = data.sort(
          (a, b) => new Date(b.created_at) - new Date(a.created_at)
        );

        setPRs(sorted);
        setFilteredPRs(sorted);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPRs();
  }, [owner, repo]);

  // Search & prioritize matches
  useEffect(() => {
    if (!searchTerm) {
      // No search → restore initial sorted order
      const sorted = [...prs].sort(
        (a, b) => new Date(b.created_at) - new Date(a.created_at)
      );
      setFilteredPRs(sorted);
    } else {
      // Search term → bring matches to top
      const matches = prs.filter((pr) =>
        pr.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      const nonMatches = prs.filter(
        (pr) => !pr.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredPRs([...matches, ...nonMatches]);
    }
  }, [searchTerm, prs]);

  if (loading) {
    return (
      <div className="flex flex-col justify-center items-center min-h-screen text-white font-sans">
        <div className="w-12 h-12 border-4 border-indigo-500 border-t-transparent rounded-full animate-spin"></div>
        <p className="mt-4 text-indigo-400">Loading pull requests...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col justify-center items-center min-h-screen text-white font-sans">
        <p className="text-red-400 text-lg font-medium">⚠️ {error}</p>
        <Link
          to="/dashboard"
          className="mt-6 px-6 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300 
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
        >
          Back to Dashboard
        </Link>
      </div>
    );
  }

  return (
    <div
      className="min-h-screen text-white px-[5%] py-10 font-sans transition-colors duration-500"
      style={{
        background:
          "linear-gradient(120deg, #00041dff 0%, #2b51ba 80%, #3b82f6 100%)",
      }}
    >
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-6">
        <h2 className="text-3xl font-bold tracking-wide">
          Pull Requests — <span className="text-indigo-200">{owner}/{repo}</span>
        </h2>
        <Link
          to="/dashboard"
          className="mt-4 md:mt-0 px-5 py-2 rounded-lg bg-red-500/20 border border-red-500 text-red-300
          hover:bg-red-500/40 hover:shadow-[0_8px_20px_rgba(239,68,68,0.4)] transition duration-300"
        >
          ⬅ Back
        </Link>
      </div>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search PR by title..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="mb-6 px-4 py-2 rounded-lg bg-white/10 placeholder-white/70 text-white 
                   focus:outline-none focus:ring-2 focus:ring-indigo-400 transition duration-300 w-full md:w-1/2"
      />

      {/* PR List */}
      {filteredPRs.length === 0 ? (
        <p className="text-indigo-100/80 text-center text-lg mt-12">
          No pull requests found.
        </p>
      ) : (
        <div className="flex flex-col gap-4">
          {filteredPRs.map((pr, i) => (
            <div
              key={pr.id}
              className="group relative p-2 rounded-xl
                 bg-gradient-to-br from-[#0a1a66] via-[#0d1b4d] to-[#020833]
                 border border-indigo-700/40 shadow-[6px_6px_12px_rgba(0,0,0,0.5)]
                 transform transition-transform duration-300 ease-out
                 hover:scale-[1.02] hover:shadow-[10px_10px_15px_rgba(99,102,241,0.5)]"
              style={{
                animation: `fadeInUp 0.5s ease forwards`,
                animationDelay: `${i * 60}ms`,
              }}
            >
              <h5 className="text-lg font-semibold mb-1 group-hover:text-indigo-300 transition">
                {pr.title}
              </h5>
              <p className="text-indigo-100/80 text-sm mb-1">
                #{pr.number} opened by <strong>{pr.user.login}</strong>
              </p>
              <p className="text-indigo-100/80 text-sm">
                Created: {new Date(pr.created_at).toLocaleDateString()}
              </p>
              <span
                className={`px-2 py-1 rounded-md text-xs font-medium mt-1 inline-block ${
                  pr.state === "open"
                    ? "bg-green-600 text-white"
                    : "bg-gray-600 text-gray-300"
                }`}
              >
                {pr.state.toUpperCase()}
              </span>
              <div className="mt-2 flex gap-2 justify-end">
                <button
                  className="px-3 py-1.5 rounded-lg bg-indigo-600/20 border border-indigo-600 text-indigo-300 
                    hover:bg-indigo-600/40 hover:transition duration-300"
                  onClick={() =>
                    window.open(pr.html_url, "_blank")
                  }
                >
                  View Repo
                </button>
                <button
                  className="px-3 py-1.5 rounded-lg bg-indigo-600/20 border border-indigo-600 text-indigo-300 
                    hover:bg-indigo-600/40 hover:transition duration-300"
                  onClick={() =>
                    navigate(`/repos/${owner}/${repo}/pulls/${pr.number}/details`)
                  }
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      <style>
        {`
          @keyframes fadeInUp {
            0% { opacity: 0; transform: translateY(10px); }
            100% { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
